//import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { useRef, useState } from 'react';
import { DirectorModel } from '../../../../types/director.type';
import DirectorForm from './DirectorForm';
import DirectorTable from './DirectorTable';

function Director() {
    const initialValues: DirectorModel = {
        DirectorID: undefined,
        DirectorName: '',
        DirectorTypeID: 0,
        OccupationID: 0,
        NationalityID: 0,
        DOB: new Date(),
        PlaceOfBirthID: 0,
        IDNumber: '',
        AppointmentDate: new Date(),
        CessationDate: new Date(),
        PermanentAddress: '',
        ComunicationAddress: '',
        ContactNumber: '',
        Email: '',
        IsWorldCheck: false,
        Remarks: '',
    };
    const [directorData, setDirectorData] = useState<DirectorModel>(initialValues);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const directorFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, director: any) => {
        event.stopPropagation();
        setDirectorData(director);
        if (directorFormFunc.current != null) {
            directorFormFunc.current?.();
        }
    }
    const clearDirectorData = () => {
        setDirectorData(initialValues);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <DirectorForm clearForm={clearDirectorData} rerenderTable={rerenderTable} directorFormFunc={directorFormFunc} directorData={directorData} />
                    </Grid>
                    <Grid item xs={12}>
                        <DirectorTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Director;
