import { Profile } from '../types/profile.type';

export function  ProfileService() {
    return {
         setProfile : (profile: Profile) => {
            if (profile != undefined) {
                localStorage.setItem('UserID', profile.UserID.toString());
                localStorage.setItem('UserName', profile.UserName);
                localStorage.setItem('RoleID', profile.RoleID.toString());
                localStorage.setItem('RoleName', profile.RoleName);
                localStorage.setItem('Token', profile.Token.toString());
                localStorage.setItem('Expiration', profile.Expiration.toString());
                localStorage.setItem('ConfigID', profile.ConfigID.toString());
            }
        },
        resetProfile: () => {
            localStorage.removeItem('UserID');
            localStorage.removeItem('UserName');
            localStorage.removeItem('RoleID');
            localStorage.removeItem('RoleName');
            localStorage.removeItem('Token');
            localStorage.removeItem('Expiration');
            localStorage.removeItem('ConfigID');
        },
        getProfile : () => {
            let profile: Profile = {
                UserID: parseInt(localStorage.getItem('UserID')!),
                UserName: localStorage.getItem('UserName')!,
                RoleID: parseInt(localStorage.getItem('RoleID')!),
                RoleName: localStorage.getItem('RoleName')!,
                Token: localStorage.getItem('Token')!,
                Expiration: new Date(localStorage.getItem('Expiration')!),
                ConfigID: parseInt(localStorage.getItem('ConfigID')!),
            };
            return profile;
        },
        isAuthencticated : () => {
            return localStorage.getItem('Token') != null;
        }
    }
}
