////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import { useState } from 'react';
import * as Yup from "yup";
import { post } from "../../../../services/common.service";
import { yupResolver } from "@hookform/resolvers/yup";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider } from '@mui/material';
import Footer from '../../../../components/Footer';

import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import { pink } from '@mui/material/colors';
import Checkbox from '@mui/material/Checkbox';

import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';

import Stack from '@mui/material/Stack';
import Slider from '@mui/material/Slider';
import VolumeDown from '@mui/icons-material/VolumeDown';
import VolumeUp from '@mui/icons-material/VolumeUp';

import Switch from '@mui/material/Switch';
import { useForm } from 'react-hook-form';

const label = { inputProps: { 'aria-label': 'Switch demo' } };

function Forms() {

    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const initialValues: {
        username: string;
        password: string;
    } = {
        username: "",
        password: "",
    };
    type UserSubmitForm = {
        username: string;
        password: string;
    };
    const validationSchema = Yup.object().shape({
        username: Yup.string()
            .required('Username is required')
            .min(2, 'Username must be at least 6 characters')
            .max(20, 'Username must not exceed 20 characters'),
        password: Yup.string()
            .required('Password is required')
            .min(2, 'Password must be at least 6 characters')
            .max(40, 'Password must not exceed 40 characters'),
    });

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<UserSubmitForm>({
        resolver: yupResolver(validationSchema)
    });
    const onSubmit = (data: UserSubmitForm) => {
        handleLogin(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const handleLogin = (formValue: { username: string; password: string }) => {
        const { username, password } = formValue;
        setMessage("gbghihiou");
        setLoading(true);
        post('token',formValue).then(
            (response:any) => {
                // history.push("/profile");
                if (response.data.token) {
                    localStorage.setItem("user", JSON.stringify(response.data));
                }
                window.location.href='/';
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setMessage(resMessage);
            }
        );
    };

    return (
        <>

            
            <Container maxWidth="sm">
                <PageTitleWrapper>
                    <PageTitle
                        heading="Login"
                        subHeading="sers."
                    />
                </PageTitleWrapper>
                <div className="register-form">
                    <form onSubmit={handleSubmit(onSubmit)}>
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={6}
                >
                    <Grid item xs={12}>
                        <Card sx={{ px: 12 }}>
                            <CardHeader title="Enter Credentials" />
                            <Divider />
                            <CardContent>
                                <Box
                                    component="form"
                                    sx={{
                                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                                    }}
                                    noValidate
                                    autoComplete="off"
                                >
                                    <div>
                                        <TextField
                                            style={{ width: '100%' }} 
                                            required
                                            id="outlined-required"
                                                    label="Username"
                                                    {...register('username')}
                                                    className={`form-control ${errors.username ? 'is-invalid' : ''}`}
                                        />
                                    </div>
                                    <div>
                                        <TextField style={{ width: '100%' }} required 
                                            id="outlined-password-input"
                                            label="Password" 
                                            type="password"
                                                    autoComplete="current-password"
                                                    {...register('password')}
                                                    className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                                        />
                                    </div>
                                </Box>
                                    </CardContent>
                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary">
                                Login
                                    </Button>
                                    <Button type="button"
                                        onClick={() => reset()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                        Reset
                                    </Button>

                        </Card>
                    </Grid>
                </Grid>
                       
                    </form>
                </div>
                <Footer />
            </Container>
           
        </>
    );
}

export default Forms;
