import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { ShareTypeForm, ShareTypeModel } from './ShareTypeForm';
import ShareTypeTable from './ShareTypeTable';

function ShareType() {
    const initualData = {
        ShareTypeName: "",
        Remarks: "",
    }
    const [shareTypeData, setShareTypeData] = useState<ShareTypeModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const shareTypeFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, shareType: any) => {
        event.stopPropagation();

        setShareTypeData(shareType);
        if (shareTypeFormFunc.current != null) {
            shareTypeFormFunc.current?.();
        }
    }
    const clearShareTypeData = () => {
        setShareTypeData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <ShareTypeForm clearForm={clearShareTypeData} rerenderTable={rerenderTable} shareTypeFormFunc={shareTypeFormFunc} shareTypeData={shareTypeData} />
                    </Grid>
                    <Grid item xs={12}>
                        <ShareTypeTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default ShareType;
