import { Profile } from '../types/profile.type';

export function  ProfileService() {
    return {
         setProfile : (profile: Profile) => {
            if (profile != undefined) {
                sessionStorage.setItem('UserID', profile.UserID.toString());
                sessionStorage.setItem('UserName', profile.UserName.toString());
                sessionStorage.setItem('RoleID', profile.RoleID.toString());
                sessionStorage.setItem('UserName', profile.RoleName.toString());
                sessionStorage.setItem('Token', profile.Token.toString());
                sessionStorage.setItem('Expiration', profile.Expiration.toString());
                sessionStorage.setItem('ConfigID', profile.ConfigID.toString());
            }
        },
        getProfile : () => {
            let profile: Profile = {
                UserID: parseInt(sessionStorage.getItem('UserID')!),
                UserName: sessionStorage.getItem('UserName')!,
                RoleID: parseInt(sessionStorage.getItem('RoleID')!),
                RoleName: sessionStorage.getItem('RoleName')!,
                Token: sessionStorage.getItem('Token')!,
                Expiration: new Date(sessionStorage.getItem('Expiration')!),
                ConfigID: parseInt(sessionStorage.getItem('RoleID')!),
            };
            return profile;
        },
        isAuthencticated : () => {
            return sessionStorage.getItem('Token') != null;
        }
    }
}
