//import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useState } from 'react';
import { CompanyTypeForm, CompanyTypeModel } from './CompanyTypeForm';
import CompanyTypeTable from './CompanyTypeTable';

function CompanyType() {
    const [companyTypeData, setCompanyTypeData] = useState<CompanyTypeModel>({
        CompanyTypeName: "abc",
        Remarks: "",
    });
    const editHandler: MouseEventHandler<HTMLButtonElement> = (companyType: any) => {
        //setUserForm(true);
        setCompanyTypeData(companyType);
    } 
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <CompanyTypeForm companyTypeData={companyTypeData} />
                    </Grid>
                    <Grid item xs={12}>
                        <CompanyTypeTable editHandler={editHandler}  />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default CompanyType;
