import  UserForm  from './UserForm';
import  UserTable  from './UserTable';
////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import { MouseEventHandler, useState } from 'react';
import * as Yup from "yup";
import { post, logout } from "../../../../services/common.service";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider } from '@mui/material';
import Footer from '../../../../components/Footer';

import Box from '@mui/material/Box';

import { useForm } from 'react-hook-form';

const label = { inputProps: { 'aria-label': 'Switch demo' } };

function UserComponent() {
    logout();
    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [userForm, setUserForm] = useState<boolean>(false);

    const onAddClick: MouseEventHandler<HTMLButtonElement> = () => {
        setUserForm(true);
    }

    return (
        <>

            <Container maxWidth="lg">
                <h1>User Management</h1>
                <Button type="button" sx={{ margin: 1 }}  variant="contained" color="primary"
                    hidden={userForm == true} onClick={onAddClick} >
                    Add User
                </Button>
                <Box hidden={userForm == false }>
                    <UserForm />
                </Box>
                <div hidden={userForm == true}>
                    <UserTable />
                </div>
                <Footer />
            </Container>

        </>
    );
}

export default UserComponent;
