////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import { useEffect, useState } from 'react';
import * as Yup from "yup";
import { post} from "../../../../services/common.service";
import { yupResolver } from "@hookform/resolvers/yup";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Typography, Autocomplete } from '@mui/material';
import Footer from '../../../../components/Footer';

import Switch from '@mui/material/Switch';
import { useForm } from 'react-hook-form';
import { DirectorModel } from '../../../../types/director.type';
import { dropdowns } from '../../../../services/dropdowns.service';
import { DirectorTypeModel } from '../../../../types/master.type';

const label = { inputProps: { 'aria-label': 'Switch demo' } };

const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    //border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};

export const DirectorForm: React.FC<any> = ({ clearForm, directorFormFunc, directorData, rerenderTable}) => {
    const [openSuccessAlert, setopenSuccessAlert] = useState<boolean>(false);
    const [directorTypes, setDirectorTypes] = useState<DirectorTypeModel[]>([]);
    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [open, setOpen] = useState(false);

    const handleOpen = () => {
        setOpen(true);
        // reset(companyTypeData);
    }
    const handleClose = () => {
        setOpen(false);
        reset(directorData);
        clearForm();
    }
    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }

        setopenSuccessAlert(false);
    };
   
    const showModal = () => {
        setOpen(true);
        reset(directorData);
    }
    const resetForm = () => {
        reset(directorData);
        clearForm();
    }
    useEffect(() => {
        directorFormFunc.current = handleOpen
        reset(directorData);
        loadDropdowns();
    }, [])

    useEffect(() => {
        reset(directorData);
    }, [directorData, directorFormFunc]);

    const loadDropdowns = () => {
        dropdowns.getDirectorTypes().then(x => {
            console.log(x);
            setDirectorTypes(x);
        }
          
        );
        //dropdowns.getDirectorTypes().then(x => setDirectorTypes(x));
        //dropdowns.getDirectorTypes().then(x => setDirectorTypes(x));
    }
    const validationSchema = Yup.object().shape({
        companyTypeName: Yup.string()
            .required('Director is required')
            .min(2, 'Director must be at least 2 characters')
            .max(20, 'Director must not exceed 20 characters'),
    });

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<DirectorModel>({
        resolver: yupResolver(validationSchema)
    });
    const onSubmit = (data: DirectorModel) => {
        handleFormSubmit(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const handleFormSubmit = (director: DirectorModel) => {
        setLoading(true);
        let _url = 'director/AddDirector';
        if (director.DirectorID != undefined) {
            _url = 'director/EditDirector';
        }
        post(_url, director).then(
            (response: any) => {
                // history.push("/profile");
                if (response.data.status == 'success') {
                    setMessage('Director Successfully Saved!');
                    setopenSuccessAlert(true);
                    handleClose();
                    rerenderTable();
                }
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setMessage(resMessage);
            }
        ); 
    };

    return (
        <>
            <Button sx={{ margin: 1 }} variant="contained" color="primary" onClick={handleOpen}>Open modal</Button>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Container maxWidth="md">
                        <div className="register-form">
                            <form onSubmit={handleSubmit(onSubmit)}>
                                <Grid
                                    container
                                    direction="row"
                                    justifyContent="center"
                                    alignItems="stretch"
                                    spacing={1}
                                >
                                    <Grid item xs={6}>
                                        <Card>
                                            <CardHeader title="Enter Company Type Detail" />
                                            <Divider />
                                            <CardContent>
                                                <Box
                                                    component="form"
                                                    sx={{
                                                        '& .MuiTextField-root': { my: 1, width: '25ch' },
                                                    }}
                                                    noValidate
                                                    autoComplete="off"
                                                >
                                                    <div>
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="DirectorName"
                                                            {...register('DirectorName')}
                                                            className={`form-control ${errors.DirectorName ? 'is-invalid' : ''}`}
                                                        />
                                                        <Autocomplete
                                                            disablePortal style={{ width: '100%' }}
                                                            id="drpDirectorType"
                                                            options={directorTypes}
                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Director Type" />}
                                                        />
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="CompanyType"
                                                            {...register('DirectorName')}
                                                            className={`form-control ${errors.DirectorName ? 'is-invalid' : ''}`}
                                                        />
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="CompanyType"
                                                            {...register('DirectorName')}
                                                            className={`form-control ${errors.DirectorName ? 'is-invalid' : ''}`}
                                                        />
                                                    </div>
                                                    <div>
                                                        <TextField style={{ width: '100%' }} required
                                                            id="outlined-password-input" label="Remarks" type="remarks" multiline rows={2} maxRows={4} autoComplete="current-password"
                                                            {...register('Remarks')}
                                                            className={`form-control ${errors.Remarks ? 'is-invalid' : ''}`}
                                                        />
                                                    </div>
                                                </Box>
                                            </CardContent>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                            <Button type="button"
                                                onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                                Reset
                                            </Button>

                                        </Card>
                                    </Grid>
                                    <Grid item xs={6}>
                                        <Card>
                                            <CardHeader title="Enter Company Type Detail" />
                                            <Divider />
                                            <CardContent>
                                                <Box
                                                    component="form"
                                                    sx={{
                                                        '& .MuiTextField-root': { my: 1, width: '25ch' },
                                                    }}
                                                    noValidate
                                                    autoComplete="off"
                                                >
                                                    <div>
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="DirectorName"
                                                            {...register('DirectorName')}
                                                            className={`form-control ${errors.DirectorName ? 'is-invalid' : ''}`}
                                                        />
                                                        <Autocomplete
                                                            disablePortal style={{ width: '100%' }}
                                                            id="drpDirectorType"
                                                            options={directorTypes}
                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Director Type" />}
                                                        />
                                                        <TextField
                                                            style={{ width: '100%' }}
