import { useEffect, useState } from 'react';
import { Get, Post } from "../../../../services/common.service";
import { Button, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Snackbar, Alert } from '@mui/material';
import { useForm, SubmitHandler } from 'react-hook-form';
import { object, string, TypeOf } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { DirectorTypeModel } from '../../../../types/master.type';
import { Height } from '@mui/icons-material';

const label = { inputProps: { 'aria-label': 'Switch demo' } };

const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    boxShadow: 24,
    p: 4,
};

export const DirectorTypeForm: React.FC<any> = ({ rerenderDirectorTypeTable, clearForm, directorTypeFormFunc, directorTypeData ,AddButtonLabel}) => {

    const [loading, setLoading] = useState<boolean>(false);
    const [openSuccessAlert, setopenSuccessAlert] = useState<boolean>(false);
    const [openErrorAlert, setopenErrorAlert] = useState<boolean>(false);
    const [errorMessage, setErrorMessage] = useState<string>("");
    const [successMessage, setSuccessMessage] = useState<string>("");
    const [open, setOpen] = useState(false);


    const handleOpen = () => {
        setOpen(true);
    }
    const handleClose = () => {
        setOpen(false);
        reset(initialValues);
        clearForm();
    }

    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }
        setopenSuccessAlert(false);
        setopenErrorAlert(false);
    };

    const initialValues: DirectorTypeModel = {
        DirectorTypeName: "",
        Remarks: "",
    };
    const showModal = () => {
        setOpen(true);
        reset(initialValues);
    }
    const resetForm = () => {
        reset(initialValues);
        clearForm();
    }
    useEffect(() => {
        if (directorTypeFormFunc != undefined) {
            directorTypeFormFunc.current = handleOpen
        }
      
        reset(directorTypeData);
    }, [])

    useEffect(() => {
        reset(directorTypeData);
        //setValue("DirectorTypeName", directorTypeData.DirectorTypeName);
    }, [directorTypeData, directorTypeFormFunc]);


    const validationSchema = object({
        DirectorTypeName: string()
            .nonempty('Director Name is required')
            .max(200, 'Director Name must be less than 200 characters'),
        Remarks: string().nonempty('Remark is required')
    });

    type RegisterInput = TypeOf<typeof validationSchema>;

    const {
        register,
        formState: { errors, isSubmitSuccessful },
        reset,
        handleSubmit,
        setValue
    } = useForm<RegisterInput>({
        resolver: zodResolver(validationSchema)
    });

    useEffect(() => {
        if (isSubmitSuccessful) {
            reset();
        }
    }, [isSubmitSuccessful]);

    const onSubmitHandler: SubmitHandler<RegisterInput> = (values) => {
        handleFormSubmit(values);
    };
    const handleFormSubmit = (formValue: DirectorTypeModel) => {
        setLoading(true);
        let _directorTypeID = formValue.DirectorTypeID ?? -1;
        Get('master/IsNameExists?table=[DirectorType]&column=DirectorTypeName&id=DirectorTypeID&idValue=' + _directorTypeID + '&columnValue=' + formValue.DirectorTypeName).then(
            (response: any) => {
                if (response.data.status == 'success') {
                    Post('master/AddDirectorType', formValue).then(
                        (response: any) => {
                            if (response.data.status == 'success') {
                                alert('successfully saved');
                                handleClose();
                                rerenderDirectorTypeTable();
                            }
                           
                        },
                        (error: any) => {
                            const resMessage =
                                (error.response &&
                                    error.response.data &&
                                    error.response.data.message) ||
                                error.message ||
                                error.toString();
                            setLoading(false);
                        }
                    );
                }
                else {
                    alert('Record Already Exists!');
                }
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setopenErrorAlert(true);
                setErrorMessage(resMessage);
            }
        );
       
    };

    return (
        <>
            <Snackbar open={openSuccessAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="success" sx={{ width: '100%' }}>
                    {successMessage}
                </Alert>
            </Snackbar>

            <Snackbar open={openErrorAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="error" sx={{ width: '100%' }}>
                    {errorMessage}
                </Alert>
            </Snackbar>
            <Button sx={{ margin: 1, height: '53px' }} variant="contained" color="primary" onClick={showModal}> {AddButtonLabel}</Button>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Container maxWidth="md">
                        <div className="register-form">
                            <Box component='form'
                                noValidate
                                autoComplete='off'
                                onSubmit={handleSubmit(onSubmitHandler)}>
                                <Grid
                                    container
                                    direction="row"
                                    justifyContent="center"
                                    alignItems="stretch"
                                    spacing={1}
                                >
                                    <Grid item xs={12}>
                                        <Card>
                                            <CardHeader title="Enter Director Type Detail" />
                                            <Divider />
                                            <CardContent>
                                                <Box
                                                    sx={{
                                                        '& .MuiTextField-root': { my: 1, width: '25ch' },
                                                    }}
                                                >
                                                    <div>
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="Director Type"
                                                            {...register('DirectorTypeName')}
                                                            fullWidth
                                                            error={!!errors['DirectorTypeName']}
                                                            helperText={errors['DirectorTypeName'] ? errors['DirectorTypeName'].message : ''}
                                                        />
                                                    </div>
                                                    <div>
                                                        <TextField style={{ width: '100%' }} required
                                                            id="outlined-password-input" label="Remarks" type="remarks" multiline rows={2} maxRows={4} autoComplete="current-password"
                                                            {...register('Remarks')}
                                                            fullWidth
                                                            error={!!errors['Remarks']}
                                                            helperText={errors['Remarks'] ? errors['Remarks'].message : ''}
                                                        />
                                                    </div>
                                                </Box>
                                            </CardContent>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                            <Button type="button"
                                                onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                                Reset
                                            </Button>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="error" onClick={handleClose}> Close</Button>
                                        </Card>
                                    </Grid>
                                </Grid>

                            </Box>
                        </div>
                    </Container>
                </Box>
        </Modal>
        </>
    );
}
