////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import { useState } from 'react';
import * as Yup from "yup";
import { post, logout } from "../../../../services/common.service";
import { yupResolver } from "@hookform/resolvers/yup";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider } from '@mui/material';
import Footer from '../../../../components/Footer';

import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { useForm } from 'react-hook-form';

const label = { inputProps: { 'aria-label': 'Switch demo' } };
export type UserModel = {
    UserName: string;
    PersonName: string;
    Email: string;
    Password: string;
    RoleID: string;
};
export function UserForm({user }) {

    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const initialValues: {
        username: string;
        password: string;
    } = {
        username: "",
        password: "",
    };
   
    const validationSchema = Yup.object().shape({
        UserName: Yup.string()
            .required('Username is required')
            .min(2, 'Username must be at least 6 characters')
            .max(20, 'Username must not exceed 20 characters'),
        Password: Yup.string()
            .required('Password is required')
            .min(2, 'Password must be at least 6 characters')
            .max(40, 'Password must not exceed 40 characters'),
        PersonName: Yup.string()
            .required('Username is required')
            .min(2, 'Username must be at least 6 characters')
            .max(20, 'Username must not exceed 20 characters'),
        Email: Yup.string()
            .required('Username is required')
            .min(2, 'Username must be at least 6 characters')
            .max(20, 'Username must not exceed 20 characters'),
        RoleID: Yup.number()
            .required('Username is required')
            .min(2, 'Username must be at least 6 characters')
            .max(20, 'Username must not exceed 20 characters'),
    });

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<UserModel>({
        resolver: yupResolver(validationSchema)
    });
    const onSubmit = (data: UserModel) => {
        setMessage("gbghihiou");
        setLoading(true);
        post('token', data).then(
            (response: any) => {
                // history.push("/profile");
                if (response.data.token) {
                    localStorage.setItem("user", JSON.stringify(response.data));
                }
                window.location.href = '/';
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setMessage(resMessage);
            }
        );
        //console.log(JSON.stringify(data, null, 2));
    };
    return (
        <>


            <Container maxWidth="sm">
                <div className="register-form">
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <Grid
                            container
                            direction="row"
                            justifyContent="center"
                            alignItems="stretch"
                            spacing={6}
                        >
                            <Grid item xs={12} >
                                <Card sx={{ px: 12 }}>
                                    <CardHeader title="Enter User Detailss" />
                                    <Divider />
                                    <CardContent>
                                        <Box
                                            component="form"
                                            sx={{
                                                '& .MuiTextField-root': { m: 1, width: '25ch' },
                                            }}
                                            noValidate
                                            autoComplete="off"
                                        >
                                            <div>
                                                <TextField
                                                    style={{ width: '100%' }} value={user.UserName}
                                                    required
                                                    id="outlined-required"
                                                    label="Username"
                                                    {...register('UserName')}
                                                    className={`form-control ${errors.UserName ? 'is-invalid' : ''}`}
                                                />
                                            </div>
                                            <div>
                                                <TextField style={{ width: '100%' }} required
                                                    id="outlined-password-input"
                                                    label="PersonName"
                                                    type="text"
                                                    {...register('PersonName')}
                                                    className={`form-control ${errors.PersonName ? 'is-invalid' : ''}`}
                                                />
                                            </div>
                                            <div>
                                                <TextField style={{ width: '100%' }} required
                                                    id="outlined-password-input"
                                                    label="Email"
                                                    type="text"
                                                    {...register('Email')}
                                                    className={`form-control ${errors.Email ? 'is-invalid' : ''}`}
                                                />
                                            </div>
                                            <div>
                                                <TextField style={{ width: '100%' }} required
                                                    id="outlined-password-input"
                                                    label="Role"
                                                    type="text"
                                                    {...register('RoleID')}
                                                    className={`form-control ${errors.RoleID ? 'is-invalid' : ''}`}
                                                />
                                            </div>
                                        </Box>
                                    </CardContent>
                                    <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary">
                                        Login
                                    </Button>
                                    <Button type="button"
                                        onClick={() => reset()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                        Reset
                                    </Button>

                                </Card>
                            </Grid>
                        </Grid>
                    </form>
                </div>
                <Footer />
            </Container>

        </>
    );
}

