import { FC, ChangeEvent, useState, ReactNode, MouseEventHandler } from 'react';
import { format } from 'date-fns';
import numeral from 'numeral';
import PropTypes from 'prop-types';
import {
    TableSortLabel, SortDirection, Tooltip, Divider, Box, FormControl, InputLabel, Card, Checkbox, IconButton, Table, TableBody, TableCell, TableHead,
    TablePagination,
    TableRow,
    TableContainer,
    Select,
    MenuItem,
    Typography,
    useTheme,
    CardHeader,
    SelectChangeEvent,
    Button
} from '@mui/material';

import { DataTableRequest, DataTableResponse, Order, Column } from '../../../../models/table.type';
import DataTable from '../../../../components/DataTables';
//import BulkActions from './BulkActions';

const LocationTable: FC<any> = ({ editHandler, rerenderTableFlag }) => {

    const columns: readonly Column[] = [
        {
            id: 'LocationID',
            numeric: true,
            disablePadding: true,
            label: 'Location ID',
            visible: false,
            searchAble: false
        },
        {
            id: 'LocationName',
            numeric: false,
            disablePadding: false,
            label: 'Location',
            visible: true,
            searchAble: true
        },
        {
            id: 'Remarks',
            numeric: false,
            disablePadding: false,
            label: 'Remarks',
            visible: true,
            searchAble: true
        },
    ];

    return (
        <Card>
            <Divider />
            <DataTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} columns={columns} url='security/GetLocations' isSearchAble={true} />
        </Card>

    );
};

export default LocationTable;
