}

interface EnhancedTableToolbarProps {
    numSelected: number;

}

const EnhancedTableToolbar = (props: EnhancedTableToolbarProps) => {
    const { numSelected } = props;

    return (
        <Toolbar
            sx={{
                pl: { sm: 2 },
                pr: { xs: 1, sm: 1 },
                ...(numSelected > 0 && {
                    bgcolor: (theme) =>
                        alpha(theme.palette.primary.main, theme.palette.action.activatedOpacity),
                }),
            }}
        >
            {numSelected > 0 ? (
                <Typography
                    sx={{ flex: '1 1 100%' }}
                    color="inherit"
                    variant="subtitle1"
                    component="div"
                >
                    {numSelected} selected
                </Typography>
            ) : (
                <Typography
                    sx={{ flex: '1 1 100%' }}
                    variant="h6"
                    id="tableTitle"
                    component="div"
                >
                    {/*Nutrition*/}
                </Typography>
            )}
            {numSelected > 0 ? (
                <Tooltip title="Delete">
                    <IconButton>
                        <DeleteIcon />
                    </IconButton>
                </Tooltip>
            ) : (<span></span>
                //<Tooltip title="Filter list">
                //    <IconButton>
                //        <FilterListIcon />
                //    </IconButton>
                //</Tooltip>
            )}
        </Toolbar>
    );
};

const RoleAccessTable: React.FC<any> = ({ columns, }) => {

    const [order, setOrder] = React.useState<Order>('asc');
    const [orderBy, setOrderBy] = React.useState<string>('');
    const [selectedAdd, setSelectedAdd] = React.useState<readonly string[]>([]);
    const [selectedEdit, setSelectedEdit] = React.useState<readonly string[]>([]);
    const [selectedDelete, setSelectedDelete] = React.useState<readonly string[]>([]);
    const [selectedView, setSelectedView] = React.useState<readonly string[]>([]);
    const [selectedReport, setSelectedReport] = React.useState<readonly string[]>([]);
    const [selectedAllAdd, setSelectedAllAdd] = React.useState<boolean>(false);
    const [selectedAllEdit, setSelectedAllEdit] = React.useState<boolean>(false);
    const [selectedAllDelete, setSelectedAllDelete] = React.useState<boolean>(false);
    const [selectedAllView, setSelectedAllView] = React.useState<boolean>(false);
    const [selectedAllReport, setSelectedAllReport] = React.useState<boolean>(false);
    const [showRoleAccessTable, setShowRoleAccessTable] = React.useState<boolean>(false);
    
    const [page, setPage] = React.useState(0);
    const [dense, setDense] = React.useState(false);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const [rows, setRows] = React.useState<InterfaceModel[]>([]);
    const [rowCount, setRowCount] = React.useState<number>(0);
    const [roles, setRoles] = React.useState<RoleModel[]>([]);
    const [selectedRole, setSelectedRole] = React.useState<RoleModel | undefined>({ RoleName: '' });
    const [totalRecords, setTotalRecords] = React.useState<number>(0);
    const [filter, setFilter] = React.useState<any>();
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<any>({
    });
    React.useEffect(() => {
        dropdowns.getAllRoles().then(x => {
            setRoles(x);
            //getFilteredData();
        })
    }, []);
    React.useEffect(() => {
            getFilteredData();
    }, [selectedRole]);
    const getFilteredData = () => {
        if (selectedRole && selectedRole.RoleID) {
                setShowRoleAccessTable(true);
                Get('Security/GetRoleAccess?roleID=' + selectedRole!.RoleID).then((response: any) => {
                    if (response.status == 200) {

                        setRows(response.data.roleAccess);
                        setTotalRecords(response.data.recordsTotal);
                    }
                },
                    (error: any) => {
                        const resMessage = (error.response && error.response.data && error.response.data.message) || error.message || error.toString();
                    }
                );
            }
        else {
            setShowRoleAccessTable(false);
        }
    };
    const resetSearchForm = () => {
        const formFields = {};
        columns.map(x => {
            if (x.numeric) {
                formFields[x.id] = 0;
            }
            else {
                formFields[x.id] = '';
            }
        });
        reset(formFields);
        setFilter(formFields);
    }
    const handleRequestSort = (
        event: React.MouseEvent<unknown>,
        property: string,
    ) => {
        const isAsc = orderBy === property && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(property);
        getFilteredData();
    };

    const handleSelectAllByEvent = (event: React.ChangeEvent<HTMLInputElement>) => {
        let _rows = [...rows];
        if (event.target.id == 'chkAdd') {
            _rows.forEach(x => x.AllowAdd = event.target.checked);
        }
        else if (event.target.id == 'chkEdit') {
            _rows.forEach(x => x.AllowEdit = event.target.checked);
        }
        else if (event.target.id == 'chkDelete') {
            _rows.forEach(x => x.AllowDelete = event.target.checked);
        }
        else if (event.target.id == 'chkView') {
            _rows.forEach(x => x.AllowView = event.target.checked);
        }
        else if (event.target.id == 'chkReport') {
            _rows.forEach(x => x.AllowReport = event.target.checked);
        }
        setRows(_rows);
    };

    const handleClick = (event: React.MouseEvent<unknown>, name: any) => {
        //const selectedIndex = selected.indexOf(name);
        //let newSelected: readonly string[] = [];

        //if (selectedIndex === -1) {
        //    newSelected = newSelected.concat(selected, name);
        //} else if (selectedIndex === 0) {
        //    newSelected = newSelected.concat(selected.slice(1));
        //} else if (selectedIndex === selected.length - 1) {
        //    newSelected = newSelected.concat(selected.slice(0, -1));
        //} else if (selectedIndex > 0) {
        //    newSelected = newSelected.concat(
        //        selected.slice(0, selectedIndex),
        //        selected.slice(selectedIndex + 1),
        //    );
        //}

        //setSelected(newSelected);
    };

    //const handleChangePage = (event: unknown, newPage: number) => {
    //    setPage(newPage);
    //    //getFilteredData();
    //};

    //const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    //    setRowsPerPage(parseInt(event.target.value, 10));
    //    setPage(0);
    //    //getFilteredData();
    //};

    const handleChangeDense = (event: React.ChangeEvent<HTMLInputElement>) => {
        setDense(event.target.checked);
    };

    const onSubmit = (data: any) => {
        if (data != undefined) {
            columns.map((column, index) => {
                if (column.numeric == true) {
                    if (data[column.id] == undefined || data[column.id] == null || data[column.id] == '') {
                        data[column.id] = 0;
                    }
                    else
                        data[column.id] = parseInt(data[column.id]);
                }
            });
        }
        setFilter(data);
        //getFilteredData();
    };

    const handleCheckboxClick = (event: React.ChangeEvent<HTMLInputElement>,interfaceID: number | undefined, eventName: string) => {
        let _rows = [...rows];
        for (var i = 0; i < rows.length; i++) {
            if (rows[i].InterfaceID == interfaceID) {
                if (eventName == 'AllowAdd') {
                    _rows[i].AllowAdd = event.target.checked;
                }
                else if (eventName == 'AllowEdit') {
                    rows[i].AllowEdit = event.target.checked;
                }
                else if (eventName == 'AllowView') {
                    rows[i].AllowView = event.target.checked;
                }
                else if (eventName == 'AllowDelete') {
                    rows[i].AllowDelete = event.target.checked;
                }
                else if (eventName == 'AllowReport') {
                    rows[i].AllowReport = event.target.checked;
                }
            }
        }
        setRows(_rows);
    }
    const handleAllCheckboxClick = (interfaceID: number | undefined, eventName: string) => {
        for (var i = 0; i < rows.length; i++) {
            if (rows[i].InterfaceID == interfaceID) {
                if (eventName == 'AllowAdd') {
                    rows[i].AllowAdd = !rows[i].AllowAdd;
                }
                else if (eventName == 'AllowEdit') {
                    rows[i].AllowEdit = !rows[i].AllowEdit;
                }
                else if (eventName == 'AllowView') {
                    rows[i].AllowView = !rows[i].AllowView;
                }
                else if (eventName == 'AllowDelete') {
                    rows[i].AllowDelete = !rows[i].AllowDelete;
                }
                else if (eventName == 'AllowReport') {
                    rows[i].AllowReport = !rows[i].AllowReport;
                }
            }
        }
        setRows(rows);
    }

    const handleSave = () => {
        if (selectedRole && selectedRole.RoleID) {
            Post('Security/UpdateRoleAccess', { RoleID: selectedRole!.RoleID, InterfaceModels : rows  }).then((response: any) => {
                if (response.status == 200) {
                    setShowRoleAccessTable(false);
                    setSelectedRole(undefined);
                    alert('success saved');
                }
            },
                (error: any) => {
                    const resMessage =
                        (error.response && error.response.data && error.response.data.message) ||
                        error.message || error.toString();
                }
            );
        }
        else {
            alert("Please select a role!");
        }
    }

    //const isSelected = (name: any) => selected.indexOf(name) !== -1;

    //const editHandlerr = (name: any) => {
    //    editHandler(name);
    //};
    // Avoid a layout jump when reaching the last page with empty rows.
    const emptyRows =
        page > 0 ? Math.max(0, (1 + page) * rowsPerPage - rows.length) : 0;

    return (
        <Box sx={{ width: '100%' }}>
            <Card>
                <CardHeader title="Role Access Management" />
                <CardContent>
                    <Grid container direction="row" justifyContent="center" alignItems="stretch" spacing={3}>
                        <Grid item xs={6}>
                            <Autocomplete onChange={(event, newValue) => {
                                    setSelectedRole(newValue!);
                            }}
                            value={selectedRole} disablePortal id="drpRole" options={roles} getOptionLabel={role => role.RoleName}
                                renderInput={(params) => <TextField  {...params} label="Role" />}
                            />
                        </Grid>
                        <Grid item xs={6}>
                            <Button sx={{ margin: 1 }} variant="contained" color="primary" onClick={handleSave}>Save</Button>
                        </Grid>
                    </Grid>
                </CardContent>
            </Card>
           
            <Divider />
            <Paper sx={{ width: '100%', mb: 2 }} hidden={showRoleAccessTable == false}>
                <Card>
                    <CardContent>
                        <TableContainer>
                            <Table  sx={{ minWidth: 750 }} aria-labelledby="tableTitle" size={dense ? 'small' : 'medium'}>
                                <TableHead>
                                    <TableRow>
                                        <TableCell padding="checkbox"> Interface </TableCell>
                                        <TableCell padding="checkbox">
                                            <Checkbox id="chkAdd" checked={rows.filter(x => x.AllowAdd).length == rows.length} color="primary" onChange={handleSelectAllByEvent}
                                                inputProps={{
                                                    'aria-label': 'select all Add', }} />
                                            Allow Add
                                        </TableCell>
                                        <TableCell padding="checkbox">
                                            <Checkbox id="chkEdit" checked={rows.filter(x => x.AllowEdit).length == rows.length} color="primary" onChange={handleSelectAllByEvent}
                                                inputProps={{
                                                    'aria-label': 'select all Edit',  }} />
                                            Allow Edit
                                        </TableCell>
                                        <TableCell padding="checkbox">
                                            <Checkbox id="chkDelete" checked={rows.filter(x => x.AllowDelete).length == rows.length} color="primary" onChange={handleSelectAllByEvent}
                                                inputProps={{
                                                    'aria-label': 'select all Delete',  }} />
                                            Allow Delete
                                        </TableCell>
                                        <TableCell padding="checkbox">
                                            <Checkbox id="chkView" checked={rows.filter(x => x.AllowView).length == rows.length} color="primary" onChange={handleSelectAllByEvent}
                                                inputProps={{
                                                    'aria-label': 'select all View',
                                                }} />
                                            Allow View
                                        </TableCell>
                                        <TableCell padding="checkbox">
                                            <Checkbox id="chkReport" checked={rows.filter(x => x.AllowReport).length == rows.length} color="primary" onChange={handleSelectAllByEvent} inputProps={{
                                                    'aria-label': 'select all Report',
                                                }} />
                                            Allow Report
                                        </TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {
                                        rows.map((row, index) => {
                                            const labelId = `enhanced-table-checkbox-${index}`;
                                            return (
                                                <TableRow key={row.InterfaceID}>
                                                    <TableCell padding="checkbox">
                                                        {row.InterfaceName}
                                                    </TableCell>
                                                    <TableCell padding="checkbox">
                                                        <Checkbox color="primary" checked={row.AllowAdd}  inputProps={{
                                                            'aria-labelledby': labelId,
                                                        }} onChange={(e) => handleCheckboxClick(e,row.InterfaceID, 'AllowAdd')} />
                                                    </TableCell>
                                                    <TableCell padding="checkbox">
                                                        <Checkbox color="primary" checked={row.AllowEdit} inputProps={{
                                                            'aria-labelledby': labelId,
                                                        }} onChange={(e) => handleCheckboxClick(e,row.InterfaceID, 'AllowEdit')} />
                                                    </TableCell>
                                                    <TableCell padding="checkbox">
                                                        <Checkbox color="primary" checked={row.AllowDelete} inputProps={{
                                                            'aria-labelledby': labelId,
                                                        }} onChange={(e) => handleCheckboxClick(e,row.InterfaceID, 'AllowDelete')} />
                                                    </TableCell>
                                                    <TableCell padding="checkbox">
                                                        <Checkbox color="primary" checked={row.AllowView} inputProps={{
                                                            'aria-labelledby': labelId,
                                                        }} onChange={(e) => handleCheckboxClick(e,row.InterfaceID, 'AllowView')} />
                                                    </TableCell>
                                                    <TableCell padding="checkbox">
                                                        <Checkbox color="primary" checked={row.AllowReport} inputProps={{
                                                            'aria-labelledby': labelId,
                                                        }} onChange={(e) => handleCheckboxClick(e,row.InterfaceID, 'AllowReport')} />
                                                    </TableCell>
                                                </TableRow>
                                            );
                                        })}
                                    {emptyRows > 0 && (
                                        <TableRow
                                            style={{
                                                height: (dense ? 33 : 53) * emptyRows,
                                            }}
                                        >
                                            <TableCell colSpan={6} />
                                        </TableRow>
                                    )}
                                </TableBody>
                            </Table>
                        </TableContainer>
                        {/*<TablePagination*/}
                        {/*    rowsPerPageOptions={[5, 10, 25, 50]}*/}
                        {/*    component="div"*/}
                        {/*    count={totalRecords}*/}
                        {/*    rowsPerPage={rowsPerPage}*/}
                        {/*    page={page}*/}
                        {/*    onPageChange={handleChangePage}*/}
                        {/*    onRowsPerPageChange={handleChangeRowsPerPage}*/}
                        {/*/>*/}
                        </CardContent>
                </Card>

               
            </Paper>
            <FormControlLabel
                control={<Switch checked={dense} onChange={handleChangeDense} />}
                label="Dense padding"
            />
        </Box>
    );
}
