import { useEffect, useState } from 'react';
import { Post, Get, Delete } from "../../../../services/common.service";
import { dropdowns } from '../../../../services/dropdowns.service';
import { Button, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Snackbar, Alert, Autocomplete, Chip } from '@mui/material';
import { useForm, SubmitHandler } from 'react-hook-form';
import { object, string, TypeOf } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import { RoleModel, LocationModel } from '../../../../types/security.type';

const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 800,
    bgcolor: 'background.paper',
    boxShadow: 24,
    p: 4,
};
export type UserModel = {
    UserID?: number;
    UserName: string;
    PersonName: string;
    Password: string;
    Email: string;
    RoleID?: number;
    RoleName: string;
    Remarks: string;
};
export const UserForm: React.FC<any> = ({ clearForm, userFormFunc, userData, rerenderUserTable, AddButtonLabel }) => {

    const [loading, setLoading] = useState<boolean>(false);
    const [roles, setRoles] = useState<RoleModel[]>([]);
    const [selectedRole, setSelectedRole] = useState<RoleModel>();
    const [locations, setLocations] = useState<LocationModel[]>([]);
    const [selectedLocations, setSelectedLocations] = useState<string[]>([]);
    const [selectedLocation, setSelectedLocation] = useState<LocationModel>();
    const [openSuccessAlert, setopenSuccessAlert] = useState<boolean>(false);
    const [openErrorAlert, setopenErrorAlert] = useState<boolean>(false);
    const [messageErorr, setErrorMessage] = useState<string>("");
    const [successMessage, setSuccessMessage] = useState<string>("");
    const [open, setOpen] = useState(false);

    const handleOpen = () => {
        setOpen(true);
    }
    const handleClose = () => {
        setOpen(false);
        reset(initialValues);
        clearForm();
    }
    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }
        setopenSuccessAlert(false);
        setopenErrorAlert(false);
    };
    const initialValues: UserModel = {
        UserName: "",
        PersonName: "",
        Password: "",
        Email: "",
        RoleID: 0,
        RoleName: "",
        Remarks: "",
    };
    const showModal = () => {
        setOpen(true);
        reset(initialValues);
    }
    const resetForm = () => {
        reset(initialValues);
        clearForm();
    }
    useEffect(() => {
        userFormFunc.current = handleOpen
        reset(userData);
        loadDropdowns();
    }, [])

    useEffect(() => {
        reset(userData);
    }, [userData, userFormFunc]);

    const validationSchema = object({
        UserName: string()
            .nonempty('User Name is required')
            .max(200, 'User Name must be less than 200 characters'),
        Password: string()
            .nonempty('Password is required')
            .max(200, 'Password must be less than 200 characters'),
        PersonName: string()
            .nonempty('Person Name is required')
            .max(200, 'Person Name must be less than 200 characters'),
        RoleName: string()
            .nonempty('Role Name is required')
            .max(200, 'Role Name must be less than 200 characters'),
        Remarks: string().nonempty('Remark is required')
    });

    const {
        register,
        formState: { errors, isSubmitSuccessful },
        reset,
        handleSubmit,
        setValue
    } = useForm<UserModel>({
        resolver: zodResolver(validationSchema)
    });

    useEffect(() => {
        if (isSubmitSuccessful) {
            reset();
        }
    }, [isSubmitSuccessful]);

    const onSubmitHandler: SubmitHandler<UserModel> = (values) => {
        handleFormSubmit(values);
    };
    const handleFormSubmit = (formValue: UserModel) => {
        setLoading(true);
        let _url = 'master/AddUser';
        if (formValue.UserID != null && formValue.UserID > 0) {
            _url = 'master/EditUser';
        }
        Post(_url, formValue).then(
            (response: any) => {
                if (response.data.status == 'success') {
                    setopenSuccessAlert(true);
                    setSuccessMessage('successfully saved');
                    handleClose();
                    rerenderUserTable();
                }
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setopenErrorAlert(true);
                setErrorMessage(resMessage);
            }
        );
    };
    const loadDropdowns = () => {
        dropdowns.getAllRoles().then(x => setRoles(x));
        dropdowns.getAllLocations().then(x => setLocations(x));
    }

    return (
        <>
            <Snackbar open={openSuccessAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="success" sx={{ width: '100%' }}>
                    {successMessage}
                </Alert>
            </Snackbar>

            <Snackbar open={openErrorAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="error" sx={{ width: '100%' }}>
                    {messageErorr}
                </Alert>
            </Snackbar>
            <Button sx={{ margin: 1, height: '53px' }} variant="contained" color="primary" onClick={showModal}> {AddButtonLabel}</Button>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Container maxWidth="md">
                        <div className="register-form">
                            <Box component='form'
                                noValidate
                                autoComplete='off'
                                onSubmit={handleSubmit(onSubmitHandler)}>
                                <Card>
                                    <CardHeader title="Enter User Type Detail" />
                                    <Divider />
                                    <CardContent>
                                        <Box sx={{ '& .MuiTextField-root': { my: 1, width: '25ch' },}} >
                                        <Grid container direction="row" justifyContent="center" alignItems="stretch" spacing={1}>
                                            <Grid item xs={6}>
                                            <div>
                                                <TextField style={{ width: '100%' }} required
                                                    id="outlined-required" label="User Name"  {...register('UserName')} fullWidth
                                                    error={!!errors['UserName']} helperText={errors['UserName'] ? errors['UserName'].message : ''} />
                                                </div>
                                                <div>
                                                    <TextField
                                                        style={{ width: '100%' }}
                                                        required
                                                        id="outlined-required"
                                                        label="Password"
                                                        {...register('Password')}
                                                        fullWidth
                                                        error={!!errors['Password']}
                                                        helperText={errors['Password'] ? errors['Password'].message : ''}
                                                    />
                                                </div>
                                                <div>
                                                    <TextField style={{ width: '100%' }} required
                                                        id="outlined-required"
                                                        label="Email"
                                                        {...register('Email')}
                                                        fullWidth
                                                        error={!!errors['Email']}
                                                        helperText={errors['Email'] ? errors['Email'].message : ''}
                                                    />
                                                </div>
                                                <Autocomplete multiple id="tags-outlined" options={locations}
                                                    getOptionLabel={(option: LocationModel) => option.LocationName}
                                                    filterSelectedOptions
                                                    renderInput={(params) => (
                                                        <TextField {...params} style={{ width: '100%' }} label="Locations" placeholder="Favorites" />
                                                    )}
                                                />
                                            </Grid>
                                            <Grid item xs={6}>
                                                <div>
                                                    <TextField
                                                        style={{ width: '100%' }}
                                                        required
                                                        id="outlined-required"
                                                        label="Person Name"
                                                        {...register('PersonName')}
                                                        fullWidth
                                                        error={!!errors['PersonName']}
                                                        helperText={errors['PersonName'] ? errors['PersonName'].message : ''}
                                                    />
                                                </div>
                                            <div >
                                                    <Autocomplete
                                                        onChange={(event, newValue) => {
                                                            setSelectedRole(newValue!);
                                                        }}
                                                        value={selectedRole}
                                                        disablePortal style={{ width: '100%' }}
                                                        id="drpRole"
                                                        options={roles}
                                                        getOptionLabel={role => role.RoleName}
                                                        renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Role" />}
                                                    />
                                            </div>
                                            <div>
                                                <TextField style={{ width: '100%' }} required
                                                    id="outlined-password-input" label="Remarks" type="remarks" multiline rows={4.5} maxRows={4.5} autoComplete="current-password"
                                                    {...register('Remarks')}
                                                    fullWidth
                                                    error={!!errors['Remarks']}
                                                    helperText={errors['Remarks'] ? errors['Remarks'].message : ''}
                                                />
                                                </div>
                                            </Grid>
                                        </Grid>
                                        </Box>
                                    </CardContent>
                                    <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                    <Button type="button"
                                        onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                        Reset
                                    </Button>
                                    <Button type="submit" sx={{ margin: 1 }} variant="contained" color="error" onClick={handleClose}> Close</Button>
                            </Card>
                      

                            </Box>
                        </div>
                    </Container>
                </Box>
            </Modal>
        </>
    );
}
