import { useEffect, useState } from 'react';
import { post } from "../../../../services/common.service";
import { Button, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Snackbar, Alert } from '@mui/material';
import { useForm, SubmitHandler } from 'react-hook-form';
import { object, string, TypeOf } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';


const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    boxShadow: 24,
    p: 4,
};
export type DesignationModel = {
    DesignationID?: number;
    DesignationName: string;
    Remarks: string;
};
export const DesignationForm: React.FC<any> = ({ clearForm, designationFormFunc, designationData, rerenderTable }) => {

    const [openAlert, setopenAlert] = useState<boolean>(false);
    const [loading, setLoading] = useState<boolean>(false);
    const [messageSuccess, setMessageSuccess] = useState<string>("");
    const [messageErorr, setMessageError] = useState<string>("");
    const [open, setOpen] = useState(false);

    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }
        setopenAlert(false);
    };
    const handleOpen = () => {
        setOpen(true);
    }
    const handleClose = () => {
        setOpen(false);
        reset(initialValues);
        clearForm();
    }
    const initialValues: DesignationModel = {
        DesignationName: "",
        Remarks: "",
    };
    const showModal = () => {
        setOpen(true);
        reset(initialValues);
    }
    const resetForm = () => {
        reset(initialValues);
        clearForm();
    }
    useEffect(() => {
        designationFormFunc.current = handleOpen
        reset(designationData);
    }, [])

    useEffect(() => {
        reset(designationData);
        setValue("DesignationName", designationData.DesignationName);
    }, [designationData, designationFormFunc]);


    const validationSchema = object({
        DesignationName: string()
            .nonempty('Designation type is required')
            .max(200, 'Designation type must be less than 200 characters'),
        Remarks: string().nonempty('Remark is required')
    });

    type RegisterInput = TypeOf<typeof validationSchema>;

    const {
        register,
        formState: { errors, isSubmitSuccessful },
        reset,
        handleSubmit,
        setValue
    } = useForm<RegisterInput>({
        resolver: zodResolver(validationSchema)
    });

    useEffect(() => {
        if (isSubmitSuccessful) {
            reset();
        }
    }, [isSubmitSuccessful]);

    const onSubmitHandler: SubmitHandler<RegisterInput> = (values) => {
        console.log(values);
        handleFormSubmit(values);
    };
    const handleFormSubmit = (formValue: DesignationModel) => {
       
        setLoading(true);

        let _url = 'master/AddDesignation';
        if (formValue.DesignationID != null && formValue.DesignationID > 0) {
            _url = 'master/EditDesignation';
        }

        post(_url, formValue).then(
            (response: any) => {
                if (response.data.status == 'success') {
                    setopenAlert(true);                    
                    setMessageSuccess('successfully saved');
                    handleClose();
                    handleClose();
                    rerenderTable();
                }
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setMessageError(resMessage);
            }
        );
    };

    return (
        <>
            <Snackbar open={openAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="success" sx={{ width: '100%' }}>
                    {messageSuccess}
                </Alert>
            </Snackbar>

            <Snackbar open={openAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="success" sx={{ width: '100%' }}>
                    {messageSuccess}
                </Alert>
            </Snackbar>

            <Button sx={{ margin: 1 }} variant="contained" color="primary" onClick={showModal}> + Add Designation</Button>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"                
            >
               

                    <Container maxWidth="md">
                        <div className="register-form">
                        <Box onSubmit={handleSubmit(onSubmitHandler)}>
                                <Grid
                                    container
                                    direction="row"
                                    justifyContent="center"
                                    alignItems="stretch"
                                    spacing={1}
                                >
                                    <Grid item xs={12}>
                                        <Card>
                                            <CardHeader title="Enter Designation  Detail" />
                                            <Divider />
                                            <CardContent>
                                                <Box
                                                    sx={{
                                                        '& .MuiTextField-root': { my: 1, width: '25ch' },
                                                    }}
                                                >
                                                    <div>
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="Designation Name"
                                                            {...register('DesignationName')}
                                                            className={`form-control ${errors.DesignationName ? 'is-invalid' : ''}`}
                                                        />
                                                    </div>
                                                    <div>
                                                        <TextField style={{ width: '100%' }}
                                                            required
                                                            id="outlined-password-input"
                                                            label="Remarks"
                                                            type="remarks"
                                                            multiline rows={2}
                                                            maxRows={4}
                                                            autoComplete="current-password"
                                                            {...register('Remarks')}
                                                            className={`form-control ${errors.Remarks ? 'is-invalid' : ''}`}
                                                        />
                                                    </div>
                                                </Box>
                                            </CardContent>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                            <Button type="button" onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary"> Reset </Button>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="error" onClick={handleClose}> Close</Button>
                                        </Card>
                                    </Grid>
                                </Grid>

                            </Box>
                        </div>
                </Container>
              
            </Modal>
        </>
    );
}
