//import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { PlaceOfBirthForm, PlaceOfBirthModel } from './PlaceOfBirthForm';
import PlaceOfBirthTable from './PlaceOfBirthTable';

function PlaceOfBirth() {
    const initualData = {
        PlaceOfBirthName: "",
        Remarks: "",
    }
    const [placeOfBirthData, setPlaceOfBirthData] = useState<PlaceOfBirthModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const placeOfBirthFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, companyType: any) => {
        event.stopPropagation();

        setPlaceOfBirthData(companyType);
        if (placeOfBirthFormFunc.current != null) {
            placeOfBirthFormFunc.current?.();
        }
    }
    const clearCompanyTypeData = () => {
        setPlaceOfBirthData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <PlaceOfBirthForm clearForm={clearCompanyTypeData} rerenderTable={rerenderTable} placeOfBirthFormFunc={placeOfBirthFormFunc} placeOfBirthData={placeOfBirthData} />
                    </Grid>
                    <Grid item xs={12}>
                        <PlaceOfBirthTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default PlaceOfBirth;
