import { get, post } from './common.service';
import { DirectorTypeModel } from '../types/master.type';

export const dropdowns = {
    getDirectorTypes: async (): Promise<DirectorTypeModel[]> => {
        return await get('Master/GetAllDirectorTypes').then(x => {
            if (x.data.status == 'success') {
                return x.data.directorTypes;
            }
            else {
                return [];
            }
        });
    },
}
