//import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { ShareholderForm, ShareholderModel } from './Share-holderForm';
import ShareholderTable from './Share-holderTable';

function Shareholder() {
    const initualData = {
        ShareholderName: "",
        Remarks: "",
    }
    const [shareholderData, setShareholderData] = useState<ShareholderModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const shareholderFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, companyType: any) => {
        event.stopPropagation();

        setShareholderData(companyType);
        if (shareholderFormFunc.current != null) {
            shareholderFormFunc.current?.();
        }
    }
    const clearCompanyTypeData = () => {
        setShareholderData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <ShareholderForm clearForm={clearCompanyTypeData} rerenderTable={rerenderTable} shareholderFormFunc={shareholderFormFunc} setShareholderData={setShareholderData} />
                    </Grid>
                    <Grid item xs={12}>
                        <ShareholderTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Shareholder;
