import { Grid, Container } from '@mui/material';

import { MouseEventHandler, useRef, useState } from 'react';
import { LocationForm, LocationModel } from './LocationForm';
import LocationTable from './LocationTable';
import Locationtable from './LocationTable';

function Location() {
    const initualData = {
        LocationName: "",
        Remarks: "",
        IsDeleted: false

    }
    const [locationData, setLocationData] = useState<LocationModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const locationFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, location: any) => {
        event.stopPropagation();

        setLocationData(location);
        if (locationFormFunc.current != null) {
            locationFormFunc.current?.();
        }
    }
    const clearLocationData = () => {
        setLocationData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <LocationForm clearForm={clearLocationData} rerenderTable={rerenderTable} locationFormFunc={locationFormFunc} locationData={locationData} />
                    </Grid>
                    <Grid item xs={12}>
                        <LocationTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Location;
