import { get, post } from './common.service';
import { DesignationModel, DirectorTypeModel, NationalityModel } from '../types/master.type';
import { PlaceOfBirthModel } from '../content/pages/Components/PlaceOfBirth/PlaceOfBirthForm';

export const dropdowns = {
    getAllDirectorTypes: async (): Promise<DirectorTypeModel[]> => {
        return await get('Master/GetAllDirectorTypes').then(x => {
            if (x.data.status == 'success') {
                return x.data.directorTypes;
            }
            else {
                return [];
            }
        });
    },
    getAllDesignationTypes: async (): Promise<DesignationModel[]> => {
        return await get('Master/GetAllDirectorTypes').then(x => {
            if (x.data.status == 'success') {
                return x.data.directorTypes;
            }
            else {
                return [];
            }
        });
    },
    getAllNationalities: async (): Promise<NationalityModel[]> => {
        return await get('Master/GetAllDirectorTypes').then(x => {
            if (x.data.status == 'success') {
                return x.data.directorTypes;
            }
            else {
                return [];
            }
        });
    },
    getAllPlaceOfBirths: async (): Promise<PlaceOfBirthModel[]> => {
        return await get('Master/GetAllDirectorTypes').then(x => {
            if (x.data.status == 'success') {
                return x.data.directorTypes;
            }
            else {
                return [];
            }
        });
    },
}
