//import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { DesignationForm, DesignationModel } from './DesignationForm';
import DesignationTable from './DesignationTable';

function Designation() {
    const initualData = {
        DesignationName: "",
        Remarks: "",
    }
    const [designationData, setDesignationData] = useState<DesignationModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const designationFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, designation: any) => {
        event.stopPropagation();

        setDesignationData(designation);
        if (designationFormFunc.current != null) {
            designationFormFunc.current?.();
        }
    }
    const clearDesignationData = () => {
        setDesignationData(initualData);
    }
    const rerenderDesignationTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <DesignationForm clearForm={clearDesignationData} rerenderDesignationTable={rerenderDesignationTable} designationFormFunc={designationFormFunc} designationData={designationData} />
                    </Grid>
                    <Grid item xs={12}>
                        <DesignationTable editHandler={editHandler} rerenderDesignationTableFlag={rerenderDesignationTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Designation;
