import * as React from 'react';
import Box from '@mui/material/Box';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import Collapse from '@mui/material/Collapse';
import Button from '@mui/material/Button';
import CloseIcon from '@mui/icons-material/Close';
import { FC } from 'react';

const TransitionAlerts: FC<any> = ({ alertType, message }) => {
    const [open, setOpen] = React.useState(true);

    return (
        <>

            <Box hidden={alertType != 'success'} sx={{ width: '100%' }}>
                    <Collapse in={open}>
                    <Alert variant="filled" severity="success"
                            action={
                                <IconButton
                                    aria-label="close"
                                    color="inherit"
                                    size="small"
                                    onClick={() => {
                                        setOpen(false);
                                    }}
                                >
                                    <CloseIcon fontSize="inherit" />
                                </IconButton>
                            }
                            sx={{ mb: 2 }}
                        >

                        {message}
                        </Alert>
                    </Collapse>
                    {/*<Button*/}
                    {/*    disabled={open}*/}
                    {/*    variant="outlined"*/}
                    {/*    onClick={() => {*/}
                    {/*        setOpen(true);*/}
                    {/*    }}*/}
                    {/*>*/}
                    {/*    Re-open*/}
                    {/*</Button>*/}
            </Box>
            <Box hidden={alertType != 'error'} sx={{ width: '100%' }}>
                <Collapse in={open}>
                    <Alert variant="filled" severity="error"
                        action={
                            <IconButton
                                aria-label="close"
                                color="inherit"
                                size="small"
                                onClick={() => {
                                    setOpen(false);
                                }}
                            >
                                <CloseIcon fontSize="inherit" />
                            </IconButton>
                        }
                        sx={{ mb: 2 }}
                    >
                        { message}
                    </Alert>
                </Collapse>
                {/*<Button*/}
                {/*    disabled={open}*/}
                {/*    variant="outlined"*/}
                {/*    onClick={() => {*/}
                {/*        setOpen(true);*/}
                {/*    }}*/}
                {/*>*/}
                {/*    Re-open*/}
                {/*</Button>*/}
            </Box>
            <Box hidden={alertType != 'info'} sx={{ width: '100%' }}>
                <Collapse in={open}>
                    <Alert variant="filled" severity="info"
                        action={
                            <IconButton
                                aria-label="close"
                                color="inherit"
                                size="small"
                                onClick={() => {
                                    setOpen(false);
                                }}
                            >
                                <CloseIcon fontSize="inherit" />
                            </IconButton>
                        }
                        sx={{ mb: 2 }}
                    >
                        {message}
                    </Alert>
                </Collapse>
                {/*<Button*/}
                {/*    disabled={open}*/}
                {/*    variant="outlined"*/}
                {/*    onClick={() => {*/}
                {/*        setOpen(true);*/}
                {/*    }}*/}
                {/*>*/}
                {/*    Re-open*/}
                {/*</Button>*/}
            </Box>
            <Box hidden={alertType != 'warning'} sx={{ width: '100%' }}>
                <Collapse in={open}>
                    <Alert variant="filled" severity="warning"
                        action={
                            <IconButton
                                aria-label="close"
                                color="inherit"
                                size="small"
                                onClick={() => {
                                    setOpen(false);
                                }}
                            >
                                <CloseIcon fontSize="inherit" />
                            </IconButton>
                        }
                        sx={{ mb: 2 }}
                    >
                        {message}
                    </Alert>
                </Collapse>
                {/*<Button*/}
                {/*    disabled={open}*/}
                {/*    variant="outlined"*/}
                {/*    onClick={() => {*/}
                {/*        setOpen(true);*/}
                {/*    }}*/}
                {/*>*/}
                {/*    Re-open*/}
                {/*</Button>*/}
            </Box>
        </>
  
    );
}
export default TransitionAlerts;
