import { useEffect, useState } from 'react';
import { Post, Delete } from "../../../../services/common.service";
import { Button, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Snackbar, Alert } from '@mui/material';
import { useForm, SubmitHandler } from 'react-hook-form';
import { object, string,number, TypeOf } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';

const label = { inputProps: { 'aria-label': 'Switch demo' } };

const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    boxShadow: 24,
    p: 4,
};
export type IDTypeModel = {
    IDTypeID?: number;
    IDTypeName: string;
    Remarks: string;
};
export const IDTypeForm: React.FC<any> = ({ clearForm, iDTypeFormFunc, iDTypeData, rerenderIDTypeTable, AddButtonLabel }) => {

    const [loading, setLoading] = useState<boolean>(false);
    const [openSuccessAlert, setOpenSuccessAlert] = useState<boolean>(false);
    const [openErrorAlert, setOpenErrorAlert] = useState<boolean>(false);
    const [successMessage, setSuccessMessage] = useState<string>("");
    const [errorMessage, setErrorMessage] = useState<string>("");
    const [open, setOpen] = useState(false);
    const [openDeleteModal, setOpenDeleteModal] = useState(false);

    const handelOpenDeleteModal = () => {
        setOpenDeleteModal(true);
    }
    const handelCloseDeleteModal = () => {
        setOpenDeleteModal(false);
    }


    const handleOpen = () => {
        setOpen(true);
    }
    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenSuccessAlert(false);
        setOpenErrorAlert(false);
    };
    const handleClose = () => {
        setOpen(false);
        reset(initialValues);
        clearForm();
    }
    const initialValues: IDTypeModel = {
        IDTypeName: "",
        Remarks: "",
    };
    const showModal = () => {
        setOpen(true);
        reset(initialValues);
    }
    const resetForm = () => {
        reset(initialValues);
        clearForm();
    }
    useEffect(() => {
        iDTypeFormFunc.current = handleOpen;
        if (iDTypeData == undefined) {
            reset(initialValues);
        }
        else {
            reset(iDTypeData);
        }
        

    }, [])

    useEffect(() => {
        if (iDTypeData == undefined) {
            reset(initialValues);
        }
        else {
            reset(iDTypeData);
        }
    }, [iDTypeData, iDTypeFormFunc]);


    const validationSchema = object({
        IDTypeID: number().optional(),
        IDTypeName: string()
            .nonempty('ID Type is required')
         .max(200, 'ID Type must be less than 200 characters'),
        Remarks: string()
    });

    type RegisterInput = TypeOf<typeof validationSchema>;

    const {
        register,
        formState: { errors, isSubmitSuccessful },
        reset,
        handleSubmit,
        setValue
    } = useForm<RegisterInput>({
        resolver: zodResolver(validationSchema)
    });

    useEffect(() => {
        if (isSubmitSuccessful) {
            reset();
        }
    }, [isSubmitSuccessful]);

    const onSubmitHandler: SubmitHandler<RegisterInput> = (values) => {
        handleFormSubmit(values);
    };
    const handleFormSubmit = (formValue: IDTypeModel) => {
        setLoading(true);
        let _url = 'master/AddIDType';
        if (formValue.IDTypeID != null && formValue.IDTypeID > 0) {
            _url = 'master/EditIDType';
        }

        Post(_url, formValue).then(
            (response: any) => {
                if (response.data.status == 'success') {
                    setOpenSuccessAlert(true);
                    setSuccessMessage('successfully saved');
                    handleClose();
                    rerenderIDTypeTable();
                }
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setOpenErrorAlert(true);
                setErrorMessage(resMessage);
            }
        );
    };

    const onDeleteHandler = () => {
        Delete('master/DeleteIDType?Id=' + iDTypeData.IDTypeID).then(
            (response: any) => {
                if (response.data.status == 'success') {
                    setOpenDeleteModal(false);
                    setOpenSuccessAlert(true);
                    setSuccessMessage('successfully Deleted');
                    rerenderIDTypeTable();
                    handleClose();
                }
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setOpenErrorAlert(true);
                setErrorMessage(resMessage);
            }
        );

    }
    const handleShowDeleteButton = (): boolean => {
        if (iDTypeData) {
            if (iDTypeData.IDTypeID != undefined) {
                return true;
            } 
        }
        return false;
    }

    return (
        <>
            <Snackbar open={openSuccessAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="success" sx={{ width: '100%' }}>
                    {successMessage}
                </Alert>
            </Snackbar>

            <Snackbar open={openErrorAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="error" sx={{ width: '100%' }}>
                    {errorMessage}
                </Alert>
            </Snackbar>

            <Button sx={{ margin: 1, height: '53px' }} variant="contained" color="primary" onClick={showModal}> {AddButtonLabel}</Button>

            <Modal
                open={openDeleteModal}
                onClose={handelCloseDeleteModal}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={1}
                >
                    <Grid item xs={12}>
                        <Card>
                            <CardHeader title="Confirm Delete !" />
                            <Divider />
                            <CardContent>
                                <Box
                                    sx={{
                                        '& .MuiTextField-root': { my: 1, width: '25ch' },
                                    }}
                                >
                                    <p>Are you sure to delete this ID  type ?</p>

                                </Box>
                            </CardContent>
                            <Button type="button" sx={{ margin: 1 }} variant="contained" color="primary" onClick={onDeleteHandler}> Yes</Button>
                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="error" onClick={handelCloseDeleteModal}> No</Button>
                        </Card>
                    </Grid>
                </Grid>
            </Modal>


            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Container maxWidth="md">
                        <div className="register-form">
                            <Box component='form'
                                noValidate
                                autoComplete='off'
                                onSubmit={handleSubmit(onSubmitHandler)}>
                                <Grid
                                    container
                                    direction="row"
                                    justifyContent="center"
                                    alignItems="stretch"
                                    spacing={1}
                                >
                                    <Grid item xs={12}>
                                        <Card>
                                            <CardHeader title="Enter ID Type Detail" />
                                            <Divider />
                                            <CardContent>
                                                <Box
                                                    sx={{
                                                        '& .MuiTextField-root': { my: 1, width: '25ch' },
                                                    }}
                                                >
                                                    <div>
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="ID Type"
                                                            {...register('IDTypeName')}
                                                            fullWidth
                                                            error={!!errors['IDTypeName']}
                                                            helperText={errors['IDTypeName'] ? errors['IDTypeName'].message : ''}
                                                        />
                                                    </div>
                                                    <div>
                                                        <TextField style={{ width: '100%' }} 
                                                            id="outlined-password-input" label="Remarks" type="remarks" multiline rows={2} maxRows={4} autoComplete="current-password"
                                                            {...register('Remarks')}
                                                        />
                                                    </div>
                                                </Box>
                                            </CardContent>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                            <Button type="button" onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary">Reset </Button>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="error" onClick={handleClose}> Close</Button>
                                            <Box component='span' hidden={handleShowDeleteButton()}>
                                                <Button type="button" sx={{ margin: 1, float: 'right' }} variant="contained" color="error" onClick={handelOpenDeleteModal}> Delete</Button>
                                            </Box>
                                        </Card>
                                    </Grid>
                                </Grid>

                            </Box>
                        </div>
                    </Container>
                </Box>
            </Modal>


        </>
    );
}
