import { Grid, Container, Autocomplete, TextField } from '@mui/material';
import { MouseEventHandler, useEffect, useRef, useState } from 'react';
import LectureTable from './LectureTable';
import { DepartmentModel, LectureModel } from '../../../../types/master.type';
import { lectureService } from '../../../../services/lecture.service';
import { studenteService } from '../../../../services/student.service';
import { ProfileService } from '../../../../services/profile.service';

const Lecture = () => {

    const [selectedLectures, setSelectedLectures] = useState<LectureModel[]>([]);
    const [departments, setDepartments] = useState<DepartmentModel[]>([]);
    const [selectedDepartment, setSelectedDepartment] = useState<DepartmentModel>();
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
