    const rerenderLectureTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>
            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                    <Autocomplete
                        onChange={(event, newValue) => {
                            setSelectedDepartment(newValue!);
                        }}
                        value={selectedDepartment}
                        disablePortal style={{ width: '100%' }}
                        id="drpNationality"
                        options={departments}
                        getOptionLabel={departments => departments.departmentName}
                        renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Nationality" />}
                    />
                    </Grid>
                    <Grid item xs={12}>
                        <LectureTable  rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Lecture;
