import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { RoleForm, RoleModel } from './RoleForm';
import RoleTable from './RoleTable';

function Role() {
    const initualData = {
        RoleName: "",
        Remarks: "",
    }
    const [roleData, setRoleData] = useState<RoleModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const roleFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, role: any) => {
        event.stopPropagation();

        setRoleData(role);
        if (roleFormFunc.current != null) {
            roleFormFunc.current?.();
        }
    }
    const clearRoleData = () => {
        setRoleData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <RoleForm clearForm={clearRoleData} rerenderTable={rerenderTable} roleFormFunc={roleFormFunc} roleData={roleData} />
                    </Grid>
                    <Grid item xs={12}>
                        <RoleTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Role;
