import { Get, Post } from './common.service';
import {  DepartmentModel } from '../types/master.type';


export const DepartmentService = {

    getDepartmentByID: async (departmentID: string): Promise<DepartmentModel[]> => {
        return await Get('department/:id/' + departmentID).then(x => {
            if (x.status == 200) {
                return x.data.departments;
            }
            else {
                return [];
            }
        });
    },
    getAllDepartments: async (): Promise<DepartmentModel[]> => {
        return await Get('department').then(x => {
            if (x.status == 200) {
                return x.data.departments;
            }
            else {
                return [];
            }
        });
    },

    
}
