import { Grid, Container, Autocomplete, TextField, Snackbar, Alert, ListItem, Checkbox, ListItemIcon, ListItemText, List, Card, Divider, CardHeader, Button, CardContent } from '@mui/material';
import { MouseEventHandler, useEffect, useRef, useState } from 'react';
import LectureTable from './LectureTable';
import { DepartmentModel, LectureModel } from '../../../../types/master.type';
import { lectureService } from '../../../../services/lecture.service';
import { studenteService } from '../../../../services/student.service';
import { ProfileService } from '../../../../services/profile.service';

function not(a: readonly number[], b: readonly number[]) {
    return a.filter((value) => b.indexOf(value) === -1);
  }
  
  function intersection(a: readonly number[], b: readonly number[]) {
    return a.filter((value) => b.indexOf(value) !== -1);
  }
  
  function union(a: readonly number[], b: readonly number[]) {
    return [...a, ...not(b, a)];
  }

const Lecture = () => {


    
    const [openSuccessAlert, setopenSuccessAlert] = useState<boolean>(false);
    const [openErrorAlert, setopenErrorAlert] = useState<boolean>(false);
    const [messageSuccess, setMessageSuccess] = useState<string>("");
    const [messageErorr, setMessageError] = useState<string>("");

    const [selectedLectures, setSelectedLectures] = useState<LectureModel[]>([]);
    const [departments, setDepartments] = useState<DepartmentModel[]>([]);
    const [selectedDepartment, setSelectedDepartment] = useState<DepartmentModel>();
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const userFormFunc = useRef<any>(null)
   
    useEffect(() => {
        lectureService.getAllDepartments().then(x => setDepartments(x));
        let _profile = ProfileService.getProfile();
        if(_profile){
            if(_profile.RoleID != 1){
                if(_profile.RoleID == 2){
                    
                }
                else if(_profile.RoleID == 3){
                    //let _student = studenteService.getStudentById(_profile.UserID);
                }
                //setSelectedDepartment(departments.filter(x => x._id == _profile.))
            }
        }
    },[]);
    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }
        setopenSuccessAlert(false);
        setopenErrorAlert(false);
    };
    const rerenderLectureTable = () => {
        setrerenderTableFlag(true);
    }

    const [checked, setChecked] = useState<readonly number[]>([]);
  const [left, setLeft] = useState<readonly number[]>([0, 1, 2, 3]);
  const [right, setRight] = useState<readonly number[]>([4, 5, 6, 7]);

  const leftChecked = intersection(checked, left);
  const rightChecked = intersection(checked, right);

  const handleToggle = (value: number) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  const numberOfChecked = (items: readonly number[]) =>
    intersection(checked, items).length;

  const handleToggleAll = (items: readonly number[]) => () => {
    if (numberOfChecked(items) === items.length) {
      setChecked(not(checked, items));
    } else {
      setChecked(union(checked, items));
    }
  };

  const handleCheckedRight = () => {
    setRight(right.concat(leftChecked));
    setLeft(not(left, leftChecked));
    setChecked(not(checked, leftChecked));
  };

  const handleCheckedLeft = () => {
    setLeft(left.concat(rightChecked));
    setRight(not(right, rightChecked));
    setChecked(not(checked, rightChecked));
  };

  const customList = (title: React.ReactNode, items: readonly number[]) => (
    <Card>
      <CardHeader
        sx={{ px: 2, py: 1 }}
        avatar={
          <Checkbox
            onClick={handleToggleAll(items)}
            checked={numberOfChecked(items) === items.length && items.length !== 0}
            indeterminate={
              numberOfChecked(items) !== items.length && numberOfChecked(items) !== 0
            }
            disabled={items.length === 0}
            inputProps={{
              'aria-label': 'all items selected',
            }}
          />
        }
        title={title}
        subheader={`${numberOfChecked(items)}/${items.length} selected`}
      />
      <Divider />
      <List
        sx={{
         
          height: 330,
          bgcolor: 'background.paper',
          overflow: 'auto',
        }}
        dense
        component="div"
        role="list"
      >
        {items.map((value: number) => {
          const labelId = `transfer-list-all-item-${value}-label`;

          return (
            <ListItem
              key={value}
              role="listitem"
              button
              onClick={handleToggle(value)}
            >
              <ListItemIcon>
                <Checkbox
                  checked={checked.indexOf(value) !== -1}
                  tabIndex={-1}
                  disableRipple
                  inputProps={{
                    'aria-labelledby': labelId,
                  }}
                />
              </ListItemIcon>
              <ListItemText id={labelId} primary={`List item ${value + 1}`} />
            </ListItem>
          );
        })}
        <ListItem />
      </List>
    </Card>
  );
    return (
        <>
         <Snackbar open={openSuccessAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="success" sx={{ width: '100%' }}>
                    {messageSuccess}
                </Alert>
            </Snackbar>

            <Snackbar open={openErrorAlert} autoHideDuration={5000} onClose={handleAlertClose}>
                <Alert onClose={handleAlertClose} variant="filled" severity="success" sx={{ width: '100%' }}>
                    {messageSuccess}
                </Alert>
            </Snackbar>
            <Card sx={{m:3}}>
            <CardHeader title="Lecture Selection" />
                <CardContent>
                <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                    <Autocomplete
                        onChange={(event, newValue) => {
                            setSelectedDepartment(newValue!);
                        }}
                        value={selectedDepartment}
                        disablePortal style={{ width: '100%' }}
                        id="drpNationality"
                        options={departments}
                        getOptionLabel={departments => departments.departmentName}
                        renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Nationality" />}
                    />
                    </Grid>
                    <Grid item xs={12}>
                    <Grid container spacing={2} justifyContent="center" alignItems="center">
                        <Grid item xs={5}>{customList('Choices', left)}</Grid>
                            <Grid item>
                            <Grid container direction="column" alignItems="center">
                            <Button
                                sx={{ my: 0.5 }}
                                variant="outlined"
                                size="small"
                                onClick={handleCheckedRight}
                                disabled={leftChecked.length === 0}
                                aria-label="move selected right"
                            >
                                &gt;
                            </Button>
                            <Button
                                sx={{ my: 0.5 }}
                                variant="outlined"
                                size="small"
                                onClick={handleCheckedLeft}
                                disabled={rightChecked.length === 0}
                                aria-label="move selected left"
                            >
                                &lt;
                            </Button>
                            </Grid>
                        </Grid>
                        <Grid item  xs={5}>{customList('Chosen', right)}</Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </Container>
                </CardContent>
            </Card>
           
        </>
    );
}

export default Lecture;
