////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import { ChangeEvent, useEffect, useState } from 'react';
import * as Yup from "yup";
import { post} from "../../../../services/common.service";
import { yupResolver } from "@hookform/resolvers/yup";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Typography, Autocomplete, Tabs, Tab, AppBar, useTheme, Fab, List, ListItem, Stack, ListItemAvatar, Avatar, ListItemText,  } from '@mui/material';
import Footer from '../../../../components/Footer';
import SwipeableViews from 'react-swipeable-views';

import Switch from '@mui/material/Switch';
import { useForm } from 'react-hook-form';
import { DirectorModel } from '../../../../types/director.type';
import { dropdowns } from '../../../../services/dropdowns.service';
import { DesignationModel, DirectorTypeModel, IDTypeModel, NationalityModel, PlaceOfBirthModel } from '../../../../types/master.type';
import Scrollbars from 'react-custom-scrollbars-2';
import { TabPanel, TabPanelProps, switchTab } from '../../../../components/TabPanel';
import { DatePicker } from '@mui/lab';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import DeleteOutlineTwoToneIcon from '@mui/icons-material/DeleteOutlineTwoTone';
import FilePresentTwoToneIcon from '@mui/icons-material/FilePresentTwoTone';
//import { DatePicker as DatePickers, KeyboardDatePicker } from "@material-ui/pickers";

const label = { inputProps: { 'aria-label': 'Switch demo' } };
const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 1000,
    bgcolor: 'background.paper',
    //border: '2px solid #000',
    boxShadow: 24,
    
};
const uploadFileRowStyle = { height: '83px', overflowX: 'auto' };
const uploadFileStyle = { padding: '30px 10px', width: '250px', 'justify-content': 'flex-start' };
const uploadFileListItemStyle = { width: '300px', minWidth: '300px', backgroundColor: 'lightgrey', borderRadius: '15px', height: '60px', boxShadow: 24 };

export const DirectorForm: React.FC<any> = ({ clearForm, directorFormFunc, directorData, rerenderTable }) => {
    const [passportFiles, setPassportFiles] = useState<File[] | null>([]);
    const [openSuccessAlert, setopenSuccessAlert] = useState<boolean>(false);
    const [dobDate, setDobDate] = useState<Date | null>(null);
    const [appointmentDate, setAppointmentDate] = useState<Date | null>(null);
    const [cessationDate, setCessationDate] = useState<Date | null>(null);
    const [directorTypes, setDirectorTypes] = useState<DirectorTypeModel[]>([]);
    const [selectedDirectorType, setSelectedDirectorType] = useState<DirectorTypeModel>();
    const [designations, setDesignations] = useState<DesignationModel[]>([]);
    const [selectedDesignation, setSelectedDesignation] = useState<DesignationModel>();
    const [nationalities, setNationalities] = useState<NationalityModel[]>([]);
    const [selectedNationality, setSelectedNationality] = useState<NationalityModel>();
    const [idTypes, setIDTypes] = useState<IDTypeModel[]>([]);
    const [selectedIdType, setselectedIdType] = useState<IDTypeModel>();
    const [placeOfBirths, setPlaceOfBirths] = useState<PlaceOfBirthModel[]>([]);
    const [selectedPlaceOfBirth, setSelectedPlaceOfBirth] = useState<PlaceOfBirthModel>();
    const [selectedTabIndex, setSelectedTabIndex] = useState(0);
    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [open, setOpen] = useState(false);
    const theme = useTheme();

    const handleOpen = () => {
        setOpen(true);
        // reset(companyTypeData);
    }
    const handleClose = () => {
        setOpen(false);
        reset(directorData);
        clearForm();
    }
    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }

        setopenSuccessAlert(false);
    };
   
    const showModal = () => {
        setOpen(true);
        reset(directorData);
    }
    const resetForm = () => {
        reset(directorData);
        clearForm();
    }
    const changeTab = (index: number) => {
        return {
            id: `simple-tab-${index}`,
            'aria-controls': `simple-tabpanel-${index}`,
        };
    }
    useEffect(() => {
        directorFormFunc.current = handleOpen
        reset(directorData);
        loadDropdowns();
    }, [])

    useEffect(() => {
        reset(directorData);
    }, [directorData, directorFormFunc]);

    const loadDropdowns = () => {
        dropdowns.getAllDirectorTypes().then(x => setDirectorTypes(x));
        dropdowns.getAllDesignations().then(x => setDesignations(x));
        dropdowns.getAllNationalities().then(x => setNationalities(x));
        dropdowns.getAllPlaceOfBirths().then(x => setPlaceOfBirths(x));
        dropdowns.getAllIDTypes().then(x => setIDTypes(x));
    }
    const validationSchema = Yup.object().shape({
        CompanyNumber: Yup.string()
            .required('Director is required')
            .min(2, 'Director must be at least 2 characters')
            .max(20, 'Director must not exceed 20 characters'),
        PlaceOfBirthID: Yup.string()
            .required('Director is required'),
        AppointmentDate: Yup.string().required('date is required')
    });

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<DirectorModel>({
        resolver: yupResolver(validationSchema)
    });
    const onClickBack = () => {
        setSelectedTabIndex(0);
        switchTab(0);
        //switchTab(1);
        //handleFormSubmit(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const onSubmit = (data: DirectorModel) => {
        //data.PlaceOfBirthID == 0;// selectedPlaceOfBirth?.PlaceOfBirthID;
        let abc = data;
        abc.PlaceOfBirthID = selectedPlaceOfBirth!.PlaceOfBirthID ?? 0;
        console.log(abc);
        setSelectedTabIndex(1);
        switchTab(1);
        
        //handleFormSubmit(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const handleFormSubmit = (director: DirectorModel) => {
        setLoading(true);
        let _url = 'director/AddDirector';
        if (director.DirectorID != undefined) {
            _url = 'director/EditDirector';
        }
        post(_url, director).then(
            (response: any) => {
                // history.push("/profile");
                if (response.data.status == 'success') {
                    setMessage('Director Successfully Saved!');
                    setopenSuccessAlert(true);
                    handleClose();
                    rerenderTable();
                }
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setMessage(resMessage);
            }
        ); 
    };

    const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
        setSelectedTabIndex(newValue);
    };
    const handleChangeIndex = (index: number) => {
        setSelectedTabIndex(index);
    };
    const handleOnChangeFile = (files: FileList, fileType: string) => {
        if (fileType == 'passport') {
            let _passportFiles = passportFiles;
            for (var i = 0; i < files.length; i++) {
                _passportFiles?.push(files[i]);
            }
            setPassportFiles(_passportFiles);
        }
    }

    return (
        <>
            <Button sx={{ margin: 1 }} variant="contained" color="primary" onClick={handleOpen}>Open modal</Button>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <form onSubmit={handleSubmit(onSubmit)} noValidate >
                <Box sx={style}>
                    <Container maxWidth="md">
                        <div className="register-form">
                            {/*   <form onSubmit={handleSubmit(onSubmit)} noValidate>*/}
                            <Grid
                                container
                                direction="row"
                                justifyContent="center"
                                alignItems="stretch"
                                
                            >
                                <Grid item xs={12}>
                                    <Card>
                                        <CardHeader title="Enter Director Detail" />
                                        <Divider />
                                        <AppBar position="static">
                                            <Tabs
                                                value={selectedTabIndex}
                                                onChange={handleTabChange}
                                                indicatorColor="secondary"
                                                textColor="inherit"
                                                variant="fullWidth"
                                                aria-label="full width tabs example"
                                            >
                                                <Tab label="Details" {...switchTab(0)} />
                                                <Tab label="Uploads" {...switchTab(1)} />
                                            </Tabs>
                                        </AppBar>
                                        <CardContent>
                                            <Box
                                                component="div"
                                                sx={{
                                                    '& .MuiTextField-root': { my: 1, width: '25ch' },
                                                }}
                                            >
                                                <SwipeableViews
                                                    axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
                                                    index={selectedTabIndex}
                                                    onChangeIndex={handleChangeIndex}
                                                >
                                                    <TabPanel value={selectedTabIndex} index={0} dir={theme.direction}>
                                                        <Grid
                                                            container
                                                            direction="row"
                                                            justifyContent="center"
                                                            alignItems="stretch"
                                                            spacing={1}
                                                        >
                                                            <Grid item xs={4}>
                                                                <div>
                                                                    <TextField
                                                                        style={{ width: '100%' }}
                                                                        required
                                                                        id="outlined-required"
                                                                        label="DirectorName"
                                                                        {...register('DirectorName')}
                                                                        className={`form-control ${errors.DirectorName ? 'is-invalid' : ''}`}
                                                                    />
                                                                        <Autocomplete
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedDirectorType(newValue!);
                                                                            }}
                                                                            value={selectedDirectorType}
                                                                        disablePortal style={{ width: '100%' }}
                                                                        id="drpDirectorType"
                                                                        options={directorTypes}
                                                                        getOptionLabel={directorTypes => directorTypes.DirectorTypeName}
                                                                        renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Director Type" />}
                                                                    />
                                                                        <Autocomplete
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedDesignation(newValue!);
                                                                            }}
                                                                            value={selectedDesignation}
                                                                        disablePortal style={{ width: '100%' }}
                                                                        id="drpDesignation"
                                                                        options={designations}
                                                                        getOptionLabel={designations => designations.DesignationName}
                                                                        renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Designation" />}
                                                                    />
                                                                        <Autocomplete
                                                                            {...register('NationalityID')}
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedNationality(newValue!);
                                                                            }}
                                                                            value={selectedNationality}
                                                                        disablePortal style={{ width: '100%' }}
                                                                        id="drpNationality"
                                                                        options={nationalities}
                                                                        getOptionLabel={nationalities => nationalities.NationalityName}
                                                                        renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Nationality" />}
                                                                    />
                                                                    <TextField
                                                                        style={{ width: '100%' }}
                                                                        required
                                                                        id="outlined-required"
                                                                        label="Company Name"
                                                                        {...register('CompanyName')}
                                                                        className={`form-control ${errors.CompanyName ? 'is-invalid' : ''}`}
                                                                        />
                                                                   
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="CompanyNumber"
                                                                            {...register('CompanyNumber')}
                                                                            className={`form-control ${errors.CompanyNumber ? 'is-invalid' : ''}`}
                                                                        />
                                                                </div>
                                                                </Grid>
                                                            <Grid item xs={4}>
                                                                    <div>
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Email"
                                                                            {...register('Email')}
                                                                            className={`form-control ${errors.Email ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <Autocomplete 
                                                                            onChange={(event, newValue) => {
                                                                                setselectedIdType(newValue!);
                                                                            }}
                                                                            value={selectedIdType}
                                                                            disablePortal style={{ width: '100%' }}
                                                                            id="drpIDType"
                                                                            options={idTypes}
                                                                            getOptionLabel={idType => idType.IDTypeName}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="ID Type" />}
                                                                        />
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="ID Number"
                                                                            {...register('IDNumber')}
                                                                            className={`form-control ${errors.IDNumber ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <DatePicker 
                                                                            label="Appointment Date"
                                                                            {...register('AppointmentDate')}
                                                                            value={appointmentDate}
                                                                            onChange={(newValue) => {
                                                                                setAppointmentDate(newValue);
                                                                            }}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} required />}
                                                                        />
                                                                        <TextField multiline rows={4.5} maxRows={4.5} 
                                                                            style={{ width: '100%' }} type="text"
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Permanant Address"
                                                                            {...register('PermanentAddress')}
                                                                            className={`form-control ${errors.PermanentAddress ? 'is-invalid' : ''}`}
                                                                        />
                                                                    </div>
                                                                </Grid>
                                                            <Grid item xs={4}>
                                                                <div>
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Contact Number"
                                                                            {...register('ContactNumber')}
                                                                            className={`form-control ${errors.ContactNumber ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <Autocomplete
                                                                            {...register('PlaceOfBirthID')}
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedPlaceOfBirth(newValue!);
                                                                            }}
                                                                            value={selectedPlaceOfBirth}
                                                                        disablePortal style={{ width: '100%' }}
                                                                        id="drpPlaceOfBirth"
                                                                        options={placeOfBirths}
                                                                        getOptionLabel={placeOfBirths => placeOfBirths.PlaceOfBirthName}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Place of Birth"
                                                                                required={true}
                                                                            />}
                                                                    />
                                                                        <DatePicker 
                                                                            label="DOB"
                                                                            {...register('DOB')}
                                                                            value={dobDate}
                                                                            onChange={(newValue) => {
                                                                                setDobDate(newValue);
                                                                            }}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} fullWidth {...params} required />}
                                                                        />
                                                                        <DatePicker
                                                                            label="Cessation Date"
                                                                            {...register('CessationDate')}
                                                                            value={cessationDate}
                                                                            onChange={(newValue) => {
                                                                                setCessationDate(newValue);
                                                                            }}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} required />}
                                                                        />
                                                                        <TextField multiline rows={4.5} maxRows={4.5} 
                                                                            style={{ width: '100%' }} type="text"
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Comunication Address"
                                                                            {...register('ComunicationAddress')}
                                                                            className={`form-control ${errors.ComunicationAddress ? 'is-invalid' : ''}`}
                                                                        />
                                                                </div>
                                                            </Grid>
                                                        </Grid>
                                                    </TabPanel>
                                                    <TabPanel value={selectedTabIndex} index={1} dir={theme.direction}>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={3.5}>
                                                                    <label htmlFor="upload-photo" >
                                                                        <input style={{ display: 'none' }} onChange={(e) => handleOnChangeFile(e.target.files, 'passport')} name="upload-photo" type="file" />
                                                                        <Fab  sx={uploadFileStyle} color="secondary" size="small" component="div" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p style={{   }}>Upload Passport</p>
                                                                        </Fab>
                                                                    </label>
                                                                   
                                                                </Grid>
                                                                <Grid item xs={ 8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                        </Stack>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="flex-start" alignItems="stretch">
                                                                <Grid item xs={3.5} >
                                                                    <label htmlFor="upload-photo">
                                                                        <input style={{ display: 'none' }} id="upload-photo" name="upload-photo" type="file" />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload Utility Bills</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                                <Grid item xs={8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                    </Stack>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={3.5}>
                                                                    <label htmlFor="upload-photo">
                                                                        <input style={{ display: 'none' }} id="upload-photo" name="upload-photo" type="file" />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload Company Docs</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                                <Grid item xs={8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText primary="Single-line item" />
                                                                        </ListItem>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                        <ListItem sx={uploadFileListItemStyle}
                                                                            secondaryAction={
                                                                                <IconButton edge="end" aria-label="delete">
                                                                                    <DeleteOutlineTwoToneIcon />
                                                                                </IconButton>
                                                                            }
                                                                        >
                                                                            <ListItemAvatar>
                                                                                <Avatar>
                                                                                    <FilePresentTwoToneIcon />
                                                                                </Avatar>
                                                                            </ListItemAvatar>
                                                                            <ListItemText
                                                                                primary="Single-line item"
                                                                            />
                                                                        </ListItem>
                                                                    </Stack>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={12}>
                                                                    <label htmlFor="upload-photo">
                                                                        <input style={{ display: 'none' }} id="upload-photo" name="upload-photo" type="file" />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload Other Docs</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={12}>
                                                                    <label htmlFor="upload-photo">
                                                                        <input style={{ display: 'none' }} id="upload-photo" name="upload-photo" type="file" />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload Word Check</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                            </Grid>
                                                            
                                                    </TabPanel>
                                                </SwipeableViews>
                                            </Box>
                                        </CardContent>
                                    </Card>
                                </Grid>
                                    <Grid item xs={12}>
                                        <Button style={{ display: selectedTabIndex == 1 ? 'none' : undefined }} type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Next</Button>
                                        <Button style={{ display: selectedTabIndex == 0 ? 'none' : undefined }} type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                        <Button onClick={() => onClickBack()} sx={{ margin: 1 }} style={{ display: selectedTabIndex == 0 ? 'none' : undefined }} type="button" variant="contained" color="primary"> Back</Button>
                                    <Button type="button"
                                        onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                        Reset
                                    </Button>

                                </Grid>
                            </Grid>

                            {/*</form>*/}
                        </div>
                    </Container>
                    </Box>
                </form>
            </Modal>


        </>
    );
}

export default DirectorForm;
