import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { NationalityForm, NationalityModel } from './NationalityForm';
import  NationalityTable from './NationalityTable';

function Nationality() {
    const initualData = {
        NationalityName: "",
        Remarks: "",
    }
    const [NationalityData, setNationalityData] = useState<NationalityModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const nationalityFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, nationality: any) => {
        event.stopPropagation();

        setNationalityData(nationality);
        if (nationalityFormFunc.current != null) {
            nationalityFormFunc.current?.();
        }
    }
    const clearNationalityData = () => {
        setNationalityData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>
            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <NationalityForm clearForm={clearNationalityData} rerenderTable={rerenderTable}
                            nationalityFormFunc={nationalityFormFunc} nationalityTypeData={NationalityData} />
                    </Grid>
                    <Grid item xs={12}>
                        <NationalityTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Nationality;
