//import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { IDTypeForm, IDTypeModel } from './IDTypeForm';
import IDTypeTable from './IDTypeTable';

function IDType() {
    const initualData = {
        IDTypeName: "",
        Remarks: "",
    }
    const [iDTypeData, setIDTypeData] = useState<IDTypeModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const iDTypeFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, companyType: any) => {
        event.stopPropagation();

        setIDTypeData(companyType);
        if (iDTypeFormFunc.current != null) {
            iDTypeFormFunc.current?.();
        }
    }
    const clearIDTypeData = () => {
        setIDTypeData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <IDTypeForm clearForm={clearIDTypeData} rerenderTable={rerenderTable} iDTypeFormFunc={iDTypeFormFunc} iDTypeData={iDTypeData} />
                    </Grid>
                    <Grid item xs={12}>
                        <IDTypeTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default IDType;
