import { Grid, Container } from '@mui/material';
import { MouseEventHandler, useRef, useState } from 'react';
import { UserForm, UserModel } from './UserForm';
import UserTable from './UserTable';

function User() {
    const initualData = {
        UserName: "",
        PersonName: "",
        Password: "",
        Email: "",
        RoleID: 0,
        RoleName: "",
        Remarks: "",
        UserLocations: []
    }
    const [userData, setUserData] = useState<UserModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const userFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, user: any) => {
        event.stopPropagation();

        setUserData(user);
        if (userFormFunc.current != null) {
            userFormFunc.current?.();
        }
    }
    const clearUserData = () => {
        setUserData(initualData);
    }
    const rerenderUserTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>
            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <UserForm clearForm={clearUserData} rerenderUserTable={rerenderUserTable}
                            userFormFunc={userFormFunc} userData={userData} AddButtonLabel="Add User" />
                    </Grid>
                    <Grid item xs={12}>
                        <UserTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default User;
