import { FC, ChangeEvent, useState, ReactNode, MouseEventHandler } from 'react';
import { format } from 'date-fns';
import numeral from 'numeral';
import PropTypes from 'prop-types';
import {
    TableSortLabel, SortDirection, Tooltip, Divider, Box, FormControl, InputLabel, Card, Checkbox, IconButton, Table, TableBody, TableCell, TableHead,
    TablePagination,
    TableRow,
    TableContainer,
    Select,
    MenuItem,
    Typography,
    useTheme,
    CardHeader,
    SelectChangeEvent,
    Button
} from '@mui/material';

import { DataTableRequest, DataTableResponse, Order, Column } from '../../../../models/table.type';
import DataTable from '../../../../components/DataTables';

const CompanyTypeTable: FC<any> = ({ editHandler, rerenderTableFlag }) => {

    const columns: readonly Column[] = [
        {
            id: 'DesignationID',
            numeric: true,
            disablePadding: true,
            label: 'Designation ID',
            visible: false,
            searchAble: false
        },
        {
            id: 'DesignationName',
            numeric: false,
            disablePadding: false,
            label: 'Designation Name',
            visible: true,
            searchAble: true
        },
        {
            id: 'Remarks',
            numeric: false,
            disablePadding: false,
            label: 'Remarks',
            visible: true,
            searchAble: true
        },
    ];

    return (
        <Card>
            <Divider />
            <DataTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} columns={columns} url='master/GetDesignations' isSearchAble={true} />
        </Card>

    );
};

export default CompanyTypeTable;
