import { Grid, Container } from '@mui/material';

import { MouseEventHandler, useRef, useState } from 'react';
import { CompanyTypeForm, CompanyTypeModel } from './CompanyTypeForm';
import CompanyTypeTable from './CompanyTypeTable';

function CompanyType() {
    const initualData = {
        CompanyTypeName: "",
        Remarks: "",
        IsDeleted: false

    }
    const [companyTypeData, setCompanyTypeData] = useState<CompanyTypeModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const companyTypeFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, companyType: any) => {
        event.stopPropagation();

        setCompanyTypeData(companyType);
        if (companyTypeFormFunc.current != null) {
            companyTypeFormFunc.current?.();
        }
    }
    const clearCompanyTypeData = () => {
        setCompanyTypeData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <CompanyTypeForm clearForm={clearCompanyTypeData} rerenderTable={rerenderTable} companyTypeFormFunc={companyTypeFormFunc} companyTypeData={companyTypeData} />
                    </Grid>
                    <Grid item xs={12}>
                        <CompanyTypeTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default CompanyType;
