////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import { useEffect, useState } from 'react';
import * as Yup from "yup";
import { post} from "../../../../services/common.service";
import { yupResolver } from "@hookform/resolvers/yup";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Typography } from '@mui/material';
import Footer from '../../../../components/Footer';

import Switch from '@mui/material/Switch';
import { useForm } from 'react-hook-form';
import { TransitionAlerts } from '../../../../components/Alert';

const label = { inputProps: { 'aria-label': 'Switch demo' } };

const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    //border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};
export type CompanyTypeModel = {
    CompanyTypeID?: number;
    CompanyTypeName: string;
    Remarks: string;
};
export const CompanyTypeForm: React.FC<any> = ({ clearForm, companyTypeFormFunc, companyTypeData, rerenderTable  }) => {

    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [open, setOpen] = useState(false);
    const handleOpen = () => {
        setOpen(true);
       // reset(companyTypeData);
    }
    const handleClose = () => {
        setOpen(false);
        reset(initialValues);
        clearForm();
    }
    const initialValues: CompanyTypeModel = {
        CompanyTypeID: 0,
        CompanyTypeName: "",
        Remarks: "",
    };
    const showModal = () => {
        setOpen(true);
        reset(initialValues);
    }
    const resetForm = () => {
        reset(initialValues);
        clearForm();
    }
    useEffect(() => {
        companyTypeFormFunc.current = handleOpen
        reset(companyTypeData);
        
    }, [])

    useEffect(() => {
        reset(companyTypeData);
    }, [companyTypeData, companyTypeFormFunc]);
    
  
    const validationSchema = Yup.object().shape({
        companyTypeName: Yup.string()
            .required('Company Type is required')
            .min(2, 'Company Type must be at least 2 characters')
            .max(200, 'Company Type must not exceed 200 characters'),
        remarks: Yup.string()
            .required('Remarks is required')
            .min(2, 'Remarks must be at least 2 characters')
    });

    const {
        register, setValue,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<CompanyTypeModel>({
       // resolver: yupResolver(validationSchema),
        shouldUnregister: false,
    });
    const onSubmit = (data: CompanyTypeModel) => {
        handleFormSubmit(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const handleFormSubmit = (formValue: { CompanyTypeName: string; Remarks: string }) => {
        const { CompanyTypeName, Remarks } = formValue;
        setMessage("gbghihiou");
        setLoading(true);
        post('master/AddCompanyType', formValue).then(
            (response: any) => {
                // history.push("/profile");
                if (response.data.status == 'success') {
                    alert('successfully saved');
                    handleClose();
                    rerenderTable();
                }
                //window.location.href = '/';
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setMessage(resMessage);
            }
        ); 
    };

    return (
        <>
            <Button sx={{ margin: 1 }} variant="contained" color="primary" onClick={showModal}>Add Company Type</Button>
          
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                    <Container maxWidth="md">
                        <div className="register-form">
                            <form onSubmit={handleSubmit(onSubmit)}>
                                <Grid
                                    container
                                    direction="row"
                                    justifyContent="center"
                                    alignItems="stretch"
                                    spacing={1}
                                >
                                    <Grid item xs={12}>
                                        <Card>
                                            <CardHeader title="Enter Company Type Detail" />
                                            <Divider />
                                            <CardContent>
                                                <Box
                                                    sx={{
                                                        '& .MuiTextField-root': { my: 1, width: '25ch' },
                                                    }}
                                                >
                                                    <div>
                                                        <TextField
                                                            style={{ width: '100%' }}
                                                            required
                                                            id="outlined-required"
                                                            label="Company Type"
                                                            {...register('CompanyTypeName')}
                                                            className={`form-control ${errors.CompanyTypeName ? 'is-invalid' : ''}`}
                                                        />
                                                    </div>
                                                    <div>
                                                        <TextField style={{ width: '100%' }} required
                                                            id="outlined-password-input" label="Remarks" type="remarks" multiline rows={2} maxRows={4} autoComplete="current-password"
                                                            {...register('Remarks')}
                                                            className={`form-control ${errors.Remarks ? 'is-invalid' : ''}`}
                                                        />
                                                    </div>
                                                </Box>
                                            </CardContent>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                            <Button type="button"
                                                onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                                Reset
                                            </Button>
                                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="error" onClick={handleClose}> Close</Button>
                                        </Card>
                                    </Grid>
                                </Grid>

                            </form>
                        </div>
                    </Container>
             
            </Modal>
          

        </>
    );
}

//export default CompanyTypeForm;
