//import { Helmet } from 'react-helmet-async';
import { Grid, Container } from '@mui/material';
import { useRef, useState } from 'react';
import { DirectorModel } from '../../../../types/director.type';
import DirectorForm from './DirectorForm';
import DirectorTable from './DirectorTable';

function Director() {
    const initualData = {
        DirectorTypeName: "",
        Remarks: "",
    }
    const [directorData, setDirectorData] = useState<DirectorModel>(initualData);
    const [rerenderTableFlag, setrerenderTableFlag] = useState<boolean>(false);
    const directorFormFunc = useRef<any>(null)
    const editHandler: any = (event: any, directorType: any) => {
        event.stopPropagation();

        setDirectorData(directorType);
        if (directorFormFunc.current != null) {
            directorFormFunc.current?.();
        }
    }
    const clearDirectorTypeData = () => {
        setDirectorData(initualData);
    }
    const rerenderTable = () => {
        setrerenderTableFlag(true);
    }
    return (
        <>

            <Container maxWidth="lg">
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={3}
                >
                    <Grid item xs={12}>
                        <DirectorForm clearForm={clearDirectorTypeData} rerenderTable={rerenderTable} directorTypeFormFunc={directorFormFunc} directorTypeData={directorData} />
                    </Grid>
                    <Grid item xs={12}>
                        <DirectorTable editHandler={editHandler} rerenderTableFlag={rerenderTableFlag} />
                    </Grid>
                </Grid>
            </Container>
        </>
    );
}

export default Director;
