////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import { useState } from 'react';
import * as Yup from "yup";
import { post} from "../../../../services/common.service";
import { yupResolver } from "@hookform/resolvers/yup";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Typography } from '@mui/material';
import Footer from '../../../../components/Footer';

import Switch from '@mui/material/Switch';
import { useForm } from 'react-hook-form';

const label = { inputProps: { 'aria-label': 'Switch demo' } };

const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};

function CompanyTypeForm() {
    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");

    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const initialValues: {
        username: string;
        password: string;
    } = {
        username: "",
        password: "",
    };
    type CompanyTypeForm = {
        companyTypeName: string;
        remarks: string;
    };
    const validationSchema = Yup.object().shape({
        companyTypeName: Yup.string()
            .required('Company Type is required')
            .min(2, 'Username must be at least 6 characters')
            .max(20, 'Username must not exceed 20 characters'),
        remarks: Yup.string()
            .required('Remarks is required')
            .min(2, 'Password must be at least 6 characters')
            .max(40, 'Password must not exceed 40 characters'),
    });

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<CompanyTypeForm>({
        resolver: yupResolver(validationSchema)
    });
    const onSubmit = (data: CompanyTypeForm) => {
        handleFormSubmit(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const handleFormSubmit = (formValue: { companyTypeName: string; remarks: string }) => {
        const { companyTypeName, remarks } = formValue;
        setMessage("gbghihiou");
        setLoading(true);
        post('token', formValue).then(
            (response: any) => {
                // history.push("/profile");
                if (response.data.token) {
                    localStorage.setItem("user", JSON.stringify(response.data));
                }
                window.location.href = '/';
            },
            (error: any) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setLoading(false);
                setMessage(resMessage);
            }
        );
    };

    return (
        <>
            <Button onClick={handleOpen}>Open modal</Button>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Typography id="modal-modal-title" variant="h6" component="h2">
                        Text in a modal
                    </Typography>
                    <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                    </Typography>
                </Box>
            </Modal>
            <Container maxWidth="md">
               
                <div className="register-form">
                    <form onSubmit={handleSubmit(onSubmit)}>
                <Grid
                    container
                    direction="row"
                    justifyContent="center"
                    alignItems="stretch"
                    spacing={6}
                >
                    <Grid item xs={12}>
                        <Card sx={{ px: 12 }}>
                            <CardHeader title="Enter Company Type Detail" />
                            <Divider />
                            <CardContent>
                                <Box
                                    component="form"
                                    sx={{
                                        '& .MuiTextField-root': { m: 1, width: '25ch' },
                                    }}
                                    noValidate
                                    autoComplete="off"
                                >
                                    <div>
                                        <TextField
                                            style={{ width: '100%' }}
                                            required
                                            id="outlined-required"
                                                    label="CompanyType"
                                                    {...register('companyTypeName')}
                                                    className={`form-control ${errors.companyTypeName ? 'is-invalid' : ''}`}
                                        />
                                    </div>
                                    <div>
                                        <TextField style={{ width: '100%' }} required
                                            id="outlined-password-input" label="Remarks"                                                        type="remarks" multiline rows={2}  maxRows={4} autoComplete="current-password"
                                                    {...register('remarks')}
                                                    className={`form-control ${errors.remarks ? 'is-invalid' : ''}`}
                                        />
                                    </div>
                                </Box>
                                    </CardContent>
                            <Button type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Save</Button>
                                    <Button type="button"
                                        onClick={() => reset()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                        Reset
                                    </Button>

                        </Card>
                    </Grid>
                </Grid>

                    </form>
                </div>
            </Container>

        </>
    );
}

export default CompanyTypeForm;
