////import { Helmet } from 'react-helmet-async';
import PageTitle from '../../../../components/PageTitle';
import React, { ChangeEvent, useEffect, useRef, useState } from 'react';
import * as Yup from "yup";
import { get, post, API_BASE_URL } from "../../../../services/common.service";
import { yupResolver } from "@hookform/resolvers/yup";

import PageTitleWrapper from '../../../../components/PageTitleWrapper';
import { Button, IconButton, Container, Grid, Card, CardHeader, CardContent, Divider, TextField, Box, Modal, Typography, Autocomplete, Tabs, Tab, AppBar, useTheme, Fab, List, ListItem, Stack, ListItemAvatar, Avatar, ListItemText, Checkbox,  } from '@mui/material';
import Footer from '../../../../components/Footer';
import SwipeableViews from 'react-swipeable-views';

import Switch from '@mui/material/Switch';
import { useForm } from 'react-hook-form';
import { DirectorModel } from '../../../../types/director.type';
import { dropdowns } from '../../../../services/dropdowns.service';
import { DesignationModel, DirectorTypeModel, IDTypeModel, NationalityModel, PlaceOfBirthModel, FileDetail } from '../../../../types/master.type';
import Scrollbars from 'react-custom-scrollbars-2';
import { TabPanel, TabPanelProps, switchTab } from '../../../../components/TabPanel';
import { DatePicker } from '@mui/lab';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import DeleteOutlineTwoToneIcon from '@mui/icons-material/DeleteOutlineTwoTone';
import FilePresentTwoToneIcon from '@mui/icons-material/FilePresentTwoTone';
import { DirectorTypeForm } from '../../../../content/pages/Components/DirectorType/DirectorTypeForm';
import { DesignationForm } from '../../../../content/pages/Components/Designation/DesignationForm';
import { IDTypeForm } from '../../../../content/pages/Components/IDType/IDTypeForm';
import { NationalityForm } from '../../../../content/pages/Components/Nationality/NationalityForm';
import { PlaceOfBirthForm } from '../../../../content/pages/Components/PlaceOfBirth/PlaceOfBirthForm';
import { Api } from '@mui/icons-material';
//import { DatePicker as DatePickers, KeyboardDatePicker } from "@material-ui/pickers";

const label = { inputProps: { 'aria-label': 'Switch demo' } };
const style = {
    position: 'absolute' as 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 1100,
    bgcolor: 'background.paper',
    //border: '2px solid #000',
    boxShadow: 24,
    
};

const uploadFileRowStyle = { height: '83px', overflowX: 'auto' };
const uploadFileStyle = { padding: '30px 10px', width: '250px', 'justify-content': 'flex-start' };
const uploadFileListItemStyle = { width: '300px', minWidth: '300px', backgroundColor: 'lightgrey', borderRadius: '15px', height: '60px', boxShadow: 24 };

export const DirectorForm: React.FC<any> = ({ clearForm, directorFormFunc, directorData, rerenderTable }) => {
    const [passportFilesDetail, setPassportFilesDetail] = useState<FileDetail[]>([]);
    const [passportFiles, setPassportFiles] = useState<File[]>([]);
    const [billFilesDetail, setBillFilesDetail] = useState<FileDetail[]>([]);
    const [billFiles, setBillFiles] = useState<File[]>([]);
    const [companyFilesDetail, setCompanyFilesDetail] = useState<FileDetail[]>([]);
    const [companyFiles, setCompanyFiles] = useState<File[]>([]);
    const [otherFilesDetail, setOtherFilesDetail] = useState<FileDetail[]>([]);
    const [otherFiles, setOtherFiles] = useState<File[]>([]);
    const [worldFilesDetail, setWorldFilesDetail] = useState<FileDetail[]>([]);
    const [worldFiles, setWorldFiles] = useState<File[]>([]);
    const [openSuccessAlert, setopenSuccessAlert] = useState<boolean>(false);
    const [dobDate, setDobDate] = useState<Date | null>(null);
    const [appointmentDate, setAppointmentDate] = useState<Date | null>(null);
    const [cessationDate, setCessationDate] = useState<Date | null>(null);
    const [directorTypes, setDirectorTypes] = useState<DirectorTypeModel[]>([]);
    const [selectedDirectorType, setSelectedDirectorType] = useState<DirectorTypeModel>();
    const [designations, setDesignations] = useState<DesignationModel[]>([]);
    const [selectedDesignation, setSelectedDesignation] = useState<DesignationModel>();
    const [nationalities, setNationalities] = useState<NationalityModel[]>([]);
    const [selectedNationality, setSelectedNationality] = useState<NationalityModel>();
    const [idTypes, setIDTypes] = useState<IDTypeModel[]>([]);
    const [selectedIdType, setselectedIdType] = useState<IDTypeModel>();
    const [placeOfBirths, setPlaceOfBirths] = useState<PlaceOfBirthModel[]>([]);
    const [selectedPlaceOfBirth, setSelectedPlaceOfBirth] = useState<PlaceOfBirthModel>();
    const [directorFormData, setDirectorFormData] = useState<DirectorModel>();
    const [selectedTabIndex, setSelectedTabIndex] = useState(0);
    const [loading, setLoading] = useState<boolean>(false);
    const [message, setMessage] = useState<string>("");
    const [open, setOpen] = useState(false);
    const theme = useTheme();

    const handleOpen = () => {
        setOpen(true);
        // reset(companyTypeData);
    }
    const handleClose = () => {
        setOpen(false);
        reset(directorData);
        clearForm();
    }
    const handleAlertClose = (event?: React.SyntheticEvent | Event, reason?: string) => {
        if (reason === 'clickaway') {
            return;
        }

        setopenSuccessAlert(false);
    };
   
    const showModal = () => {
        setOpen(true);
        reset(directorData);
    }
    const resetForm = () => {
        reset(directorData);
        clearForm();
    }
    const changeTab = (index: number) => {
        return {
            id: `simple-tab-${index}`,
            'aria-controls': `simple-tabpanel-${index}`,
        };
    }
    useEffect(() => {
        directorFormFunc.current = handleOpen
        reset(directorData);
        loadDropdowns();
    }, [])

    useEffect(() => {
        reset(directorData);
        editForm(directorData);
    }, [directorData, directorFormFunc]);
    const editForm = (data: DirectorModel) => {
        setselectedIdType({ IDTypeID: directorData.IDTypeID, IDTypeName: directorData.IDTypeName });
        setSelectedNationality({ NationalityID: directorData.NationalityID, NationalityName: directorData.NationalityName });
        setSelectedDesignation({ DesignationID: directorData.DesignationID, DesignationName: directorData.DesignationName });
        setSelectedDirectorType({ DirectorTypeID: directorData.DirectorTypeID, DirectorTypeName: directorData.DirectorTypeName });
        setSelectedPlaceOfBirth({ PlaceOfBirthID: directorData.PlaceOfBirthID, PlaceOfBirthName: directorData.PlaceOfBirthName });
        setAppointmentDate(new Date(data.AppointmentDate));
        setCessationDate(new Date(data.CessationDate));
        setDobDate(new Date(data.DOB));
        if (data.DirectorID) {
            get('Director/GetDirectorFiles?DirectorID=' + data.DirectorID).then(x => {

                setPassportFilesDetail(x.data.PassportFiles);
                setBillFilesDetail(x.data.BillFiles);
                setCompanyFilesDetail(x.data.CompanyFiles);
                setOtherFilesDetail(x.data.OtherFiles);
                setWorldFilesDetail(x.data.WorldFiles);
            });
        }
    }
    const loadDropdowns = () => {
        dropdowns.getAllDirectorTypes().then(x => setDirectorTypes(x));
        dropdowns.getAllDesignations().then(x => setDesignations(x));
        dropdowns.getAllNationalities().then(x => setNationalities(x));
        dropdowns.getAllPlaceOfBirths().then(x => setPlaceOfBirths(x));
        dropdowns.getAllIDTypes().then(x => { console.log(x); setIDTypes(x) });
    }
    const validationSchema = Yup.object().shape({
        CompanyNumber: Yup.string()
            .required('Director is required')
            .min(2, 'Director must be at least 2 characters')
            .max(20, 'Director must not exceed 20 characters'),
        PlaceOfBirthID: Yup.string()
            .required('Director is required'),
        AppointmentDate: Yup.string().required('date is required')
    });

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors }
    } = useForm<DirectorModel>({
        resolver: yupResolver(validationSchema)
    });
    const onClickBack = () => {
        setSelectedTabIndex(0);
        switchTab(0);
        //switchTab(1);
        //handleFormSubmit(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const onSubmit = (data: DirectorModel) => {
        data.PlaceOfBirthID = selectedPlaceOfBirth!.PlaceOfBirthID ?? 0;
        data.DesignationID = selectedDesignation!.DesignationID ?? 0;
        data.DirectorTypeID = selectedDirectorType!.DirectorTypeID ?? 0;
        data.NationalityID = selectedNationality!.NationalityID ?? 0;
        data.IDTypeID = selectedIdType!.IDTypeID ?? 0;
        data.AppointmentDate = new Date(data.AppointmentDate);
        console.log(data);
        setDirectorFormData(data);
        setSelectedTabIndex(1);
        switchTab(1);
        
        //handleFormSubmit(data);
        //console.log(JSON.stringify(data, null, 2));
    };
    const handleSave = () => {
        let formData = new FormData();
        formData.append("Director", JSON.stringify(directorFormData));
        for (var i = 1; i <= passportFiles.length; i++) {
            formData.append('PassportFiles', passportFiles[i - 1]);
        }
        for (var i = 1; i <= billFiles.length; i++) {
            formData.append('BillFiles', billFiles[i - 1]);
        }
        for (var i = 1; i <= companyFiles.length; i++) {
            formData.append('CompanyFiles', companyFiles[i - 1]);
        }
        for (var i = 1; i <= otherFiles.length; i++) {
            formData.append('OtherFiles', otherFiles[i - 1]);
        }
        for (var i = 1; i <= worldFiles.length; i++) {
            formData.append('WorldFiles', worldFiles[i - 1]);
        }
        handleFormSubmit(formData);
    };
    const handleFormSubmit = (formData: FormData) => {
        setLoading(true);
        let _url = 'director/AddDirectorAsync';
            if (directorFormData!.DirectorID) {
                _url = 'director/EditDirectorAsync';
            }
        post(_url, formData).then(
                (response: any) => {
                    // history.push("/profile");
                    if (response.data.status == 'success') {
                        setMessage('Director Successfully Saved!');
                        setopenSuccessAlert(true);
                        handleClose();
                        rerenderTable();
                    }
                },
                (error: any) => {
                    const resMessage =
                        (error.response &&
                            error.response.data &&
                            error.response.data.message) ||
                        error.message ||
                        error.toString();
                    setLoading(false);
                    setMessage(resMessage);
                }
            ); 
      
    };

    const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
        setSelectedTabIndex(newValue);
    };
    const handleChangeIndex = (index: number) => {
        setSelectedTabIndex(index);
    };
    const handleChangeFile = event => {
        if (event.target.id == 'filePassport') {
            let _passportFiles = passportFiles;
            _passportFiles.push(...event.target.files);
            setPassportFiles(_passportFiles);
            let _passportFilesDetail: FileDetail[] = []; 
            for (let i = 0; i < _passportFiles.length; i++) {
                _passportFilesDetail.push({ Name: _passportFiles[i].name});
            }
            setPassportFilesDetail(_passportFilesDetail);
        }
        else if (event.target.id == 'fileBills') {
            let _billFiles = billFiles;
            for (var i = 0; i < event!.target!.files!.length; i++) {
                _billFiles?.push(event!.target!.files![i]);
            }
            setBillFiles(_billFiles);
            let _billFilesDetail: FileDetail[] = [];
            for (let i = 0; i < _billFiles.length; i++) {
                _billFilesDetail.push({ Name: _billFiles[i].name });
            }
            setPassportFilesDetail(_billFilesDetail);
        }
        else if (event.target.id == 'fileCompanyDocs') {
            let _companyFiles = companyFiles;
            for (var i = 0; i < event!.target!.files!.length; i++) {
                _companyFiles?.push(event!.target!.files![i]);
            }
            setCompanyFiles(_companyFiles);
            let _companyFilesDetail: FileDetail[] = [];
            for (let i = 0; i < _companyFiles.length; i++) {
                _companyFilesDetail.push({ Name: _companyFiles[i].name });
            }
            setCompanyFilesDetail(_companyFilesDetail);
        }
        else if (event.target.id == 'fileOtherDocs') {
            let _otherFiles = otherFiles;
            for (var i = 0; i < event!.target!.files!.length; i++) {
                _otherFiles?.push(event!.target!.files![i]);
            }
            setOtherFiles(_otherFiles);
            let _otherFilesDetail: FileDetail[] = [];
            for (let i = 0; i < _otherFiles.length; i++) {
                _otherFilesDetail.push({ Name: _otherFiles[i].name });
            }
            setOtherFilesDetail(_otherFilesDetail);
        }
        else if (event.target.id == 'fileWorld') {
            let _worldFiles = worldFiles;
            for (var i = 0; i < event!.target!.files!.length; i++) {
                _worldFiles?.push(event!.target!.files![i]);
            }
            setWorldFiles(_worldFiles);
            let _worldFilesDetail: FileDetail[] = [];
            for (let i = 0; i < _worldFiles.length; i++) {
                _worldFilesDetail.push({ Name: _worldFiles[i].name });
            }
            setWorldFilesDetail(_worldFilesDetail);
        }
        
    };
    const rerenderDirectorTypeTable = () => {
        dropdowns.getAllDirectorTypes().then(x => setDirectorTypes(x));
    }
    const rerenderDesignationTable = () => {
        dropdowns.getAllDesignations().then(x => setDesignations(x));
    }
    const rerenderIDTypeTable = () => {
        dropdowns.getAllIDTypes().then(x => setIDTypes(x));
    }
    const rerenderNationalityTable = () => {
        dropdowns.getAllNationalities().then(x => setNationalities(x));
    }
    const rerenderPlaceOfBirthTable = () => {
        dropdowns.getAllPlaceOfBirths().then(x => setPlaceOfBirths(x));
    }
    const handleShowFile = (file: FileDetail) => {
        if (file.Path != undefined && file.Path != '') {
            window.open(API_BASE_URL + 'Content/Company/Director/' + file.Name, '_blank', 'noreferrer');
        }
    }
    const handleDeleteFile = (file: FileDetail, category: string) => {
        if (file.Path != undefined && file.Path != '') {
            window.open(API_BASE_URL + 'Content/Company/Director/' + file.Name, '_blank', 'noreferrer');
        }
        if (category == 'passport') {
            let _passportFiles = passportFiles.filter(x => x.name != file.Name);
            setPassportFiles(_passportFiles);
            let _passsportFilesDetail = passportFilesDetail.filter(x => x.Name != file.Name);
            setPassportFilesDetail(_passsportFilesDetail);
        }
    }
    return (
        <>
            <Button sx={{ margin: 1 }} variant="contained" color="primary" onClick={handleOpen}>Open modal</Button>
            <Modal open={open}  onClose={handleClose}  aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description" >
                <form onSubmit={handleSubmit(onSubmit)} noValidate >
                <Box sx={style}>
                    <Container maxWidth="md">
                        <div className="register-form">
                            {/*   <form onSubmit={handleSubmit(onSubmit)} noValidate>*/}
                            <Grid container direction="row" justifyContent="center" alignItems="stretch">
                                <Grid item xs={12}>
                                    <Card>
                                        <CardHeader title="Enter Director Detail" />
                                        <Divider />
                                        <AppBar position="static">
                                            <Tabs
                                                value={selectedTabIndex}
                                                onChange={handleTabChange}
                                                indicatorColor="secondary"
                                                textColor="inherit"
                                                variant="fullWidth"
                                                aria-label="full width tabs example"
                                            >
                                                <Tab label="Details" {...switchTab(0)} />
                                                <Tab label="Uploads" {...switchTab(1)} />
                                            </Tabs>
                                        </AppBar>
                                        <CardContent>
                                            <Box component="div" sx={{ '& .MuiTextField-root': { my: 1, width: '25ch' }, }}  >
                                                <SwipeableViews  axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'} index={selectedTabIndex} onChangeIndex={handleChangeIndex}
                                                >
                                                    <TabPanel value={selectedTabIndex} index={0} dir={theme.direction}>
                                                        <Grid
                                                            container
                                                            direction="row"
                                                            justifyContent="center"
                                                            alignItems="stretch"
                                                            spacing={1}
                                                        >
                                                            <Grid item xs={3.6}>
                                                                <div>
                                                                    <TextField
                                                                        style={{ width: '100%' }}
                                                                        required
                                                                        id="outlined-required"
                                                                        label="Director Name"
                                                                        {...register('DirectorName')}
                                                                        className={`form-control ${errors.DirectorName ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Company Name"
                                                                            {...register('CompanyName')}
                                                                            className={`form-control ${errors.CompanyName ? 'is-invalid' : ''}`}
                                                                        />

                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="CompanyNumber"
                                                                            {...register('CompanyNumber')}
                                                                            className={`form-control ${errors.CompanyNumber ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <DatePicker
                                                                            label="Appointment Date"
                                                                            {...register('AppointmentDate')}
                                                                            value={appointmentDate}
                                                                            onChange={(newValue) => {
                                                                                setAppointmentDate(newValue);
                                                                            }}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} required />}
                                                                        />
                                                                        <DatePicker
                                                                            label="DOB"
                                                                            {...register('DOB')}
                                                                            value={dobDate}
                                                                            onChange={(newValue) => {
                                                                                setDobDate(newValue);
                                                                            }}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} fullWidth {...params} required />}
                                                                        />
                                                                        <DatePicker
                                                                            label="Cessation Date"
                                                                            {...register('CessationDate')}
                                                                            value={cessationDate}
                                                                            onChange={(newValue) => {
                                                                                setCessationDate(newValue);
                                                                            }}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} required />}
                                                                        />
                                                                   
                                                                </div>
                                                            </Grid>
                                                            <Grid item xs={3.6}>
                                                                <div>
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Email"
                                                                            {...register('Email')}
                                                                            className={`form-control ${errors.Email ? 'is-invalid' : ''}`}
                                                                        />
                                                                       
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="ID Number"
                                                                            {...register('IDNumber')}
                                                                            className={`form-control ${errors.IDNumber ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <TextField
                                                                            style={{ width: '100%' }}
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Contact Number"
                                                                            {...register('ContactNumber')}
                                                                            className={`form-control ${errors.ContactNumber ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <TextField multiline 
                                                                            style={{ width: '100%' }} type="text"
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Permanant Address"
                                                                            {...register('PermanentAddress')}
                                                                            className={`form-control ${errors.PermanentAddress ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <TextField multiline
                                                                            style={{ width: '100%' }} type="text"
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Comunication Address"
                                                                            {...register('ComunicationAddress')}
                                                                            className={`form-control ${errors.ComunicationAddress ? 'is-invalid' : ''}`}
                                                                        />
                                                                        <Checkbox
                                                                            color="primary"
                                                                            checked={true}
                                                                            inputProps={{
                                                                                'aria-labelledby': 'Is World Check',
                                                                            }}
                                                                        />
                                                                        <label>Is World Check</label>
                                                                    </div>
                                                            </Grid>
                                                            <Grid item xs={3.6}>
                                                                <div>
                                                                        <Autocomplete
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedDirectorType(newValue!);
                                                                            }}
                                                                            value={selectedDirectorType}
                                                                            disablePortal style={{ width: '100%' }}
                                                                            id="drpDirectorType"
                                                                            options={directorTypes}
                                                                            getOptionLabel={directorTypes => directorTypes.DirectorTypeName}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Director Type" />}
                                                                        />
                                                                        <Autocomplete
                                                                            onChange={(event, newValue) => {
                                                                                setselectedIdType(newValue!);
                                                                            }}
                                                                            value={selectedIdType}
                                                                            disablePortal
                                                                            id="drpIDType"
                                                                            options={idTypes}
                                                                            getOptionLabel={idType => idType.IDTypeName}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="ID Type" />}
                                                                        />
                                                                        <Autocomplete
                                                                            {...register('PlaceOfBirthID')}
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedPlaceOfBirth(newValue!);
                                                                            }}
                                                                            value={selectedPlaceOfBirth}
                                                                        disablePortal style={{ width: '100%' }}
                                                                        id="drpPlaceOfBirth"
                                                                        options={placeOfBirths}
                                                                        getOptionLabel={placeOfBirths => placeOfBirths.PlaceOfBirthName}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Place of Birth"
                                                                                required={true}
                                                                            />}
                                                                        />
                                                                       
                                                                        <Autocomplete
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedDesignation(newValue!);
                                                                            }}
                                                                            value={selectedDesignation}
                                                                            disablePortal style={{ width: '100%' }}
                                                                            id="drpDesignation"
                                                                            options={designations}
                                                                            getOptionLabel={designations => designations.DesignationName}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Designation" />}
                                                                        />
                                                                        <Autocomplete
                                                                            {...register('NationalityID')}
                                                                            onChange={(event, newValue) => {
                                                                                setSelectedNationality(newValue!);
                                                                            }}
                                                                            value={selectedNationality}
                                                                            disablePortal style={{ width: '100%' }}
                                                                            id="drpNationality"
                                                                            options={nationalities}
                                                                            getOptionLabel={nationalities => nationalities.NationalityName}
                                                                            renderInput={(params) => <TextField style={{ width: '100%' }} {...params} label="Nationality" />}
                                                                        />
                                                                        <TextField 
                                                                            style={{ width: '100%' }} type="text"
                                                                            required
                                                                            id="outlined-required"
                                                                            label="Remarks"
                                                                            {...register('Remarks')}
                                                                            className={`form-control ${errors.Remarks ? 'is-invalid' : ''}`}
                                                                        />
                                                                       
                                                                </div>
                                                            </Grid>
                                                                <Grid item xs={1.2}>
                                                                    <DirectorTypeForm xs={{ mb: 3 }} clearForm={() => { }} rerenderDirectorTypeTable={rerenderDirectorTypeTable} directorTypeFormFunc={useRef<any>(null)} directorTypeData={selectedDirectorType} AddButtonLabel="Add" />
                                                                    <IDTypeForm clearForm={() => { }} rerenderIDTypeTable={rerenderIDTypeTable} iDTypeFormFunc={useRef<any>(null)} idTypeData={selectedIdType} AddButtonLabel="Add" />
                                                                    <PlaceOfBirthForm clearForm={() => { }} rerenderPlaceOfBirthTable={rerenderPlaceOfBirthTable} placeOfBirthFormFunc={useRef<any>(null)} directorTypeData={selectedPlaceOfBirth} AddButtonLabel="Add" />
                                                                    <DesignationForm clearForm={() => { }} rerenderDesignationTable={rerenderDesignationTable} designationFormFunc={useRef<any>(null)} designationData={selectedDesignation} AddButtonLabel="Add"  />
                                                                    
                                                                    <NationalityForm clearForm={() => { }} rerenderNationalityTable={rerenderNationalityTable} nationalityFormFunc={useRef<any>(null)} nationalityData={selectedNationality} AddButtonLabel="Add" />
                                                                    
                                                            </Grid>
                                                        </Grid>
                                                    </TabPanel>
                                                    <TabPanel value={selectedTabIndex} index={1} dir={theme.direction}>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={3.5}>
                                                                    <label htmlFor="filePassport" >
                                                                        <input style={{ display: 'none' }} onChange={handleChangeFile} id="filePassport" name="upload-photo" type="file" multiple />
                                                                        <Fab  sx={uploadFileStyle} color="secondary" size="small" component="div" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p style={{   }}>Upload Passport</p>
                                                                        </Fab>
                                                                    </label>
                                                                   
                                                                </Grid>
                                                                <Grid item xs={ 8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        {
                                                                            passportFilesDetail.map(x => {
                                                                                return (
                                                                                    <ListItem sx={uploadFileListItemStyle}
                                                                                        secondaryAction={
                                                                                            <IconButton edge="end" aria-label="delete" onClick={() => handleDeleteFile(x,'passport') }>
                                                                                                <DeleteOutlineTwoToneIcon />
                                                                                            </IconButton>
                                                                                        }
                                                                                    >
                                                                                        <ListItemAvatar>
                                                                                            <Avatar>
                                                                                                <FilePresentTwoToneIcon />
                                                                                            </Avatar>
                                                                                        </ListItemAvatar>
                                                                                        <ListItemText
                                                                                            primary={x.Name}
                                                                                        />
                                                                                    </ListItem>
                                                                                    )
                                                                            })
                                                                        }
                                                                       
                                                                        </Stack>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="flex-start" alignItems="stretch">
                                                                <Grid item xs={3.5} >
                                                                    <label htmlFor="fileBills">
                                                                        <input style={{ display: 'none' }} onChange={handleChangeFile} id="fileBills" name="upload-bill" type="file" multiple />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload Utility Bills</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                                <Grid item xs={8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        {
                                                                            billFilesDetail.map(x => {
                                                                                return (
                                                                                    <ListItem sx={uploadFileListItemStyle}
                                                                                        secondaryAction={
                                                                                            <IconButton edge="end" aria-label="delete">
                                                                                                <DeleteOutlineTwoToneIcon />
                                                                                            </IconButton>
                                                                                        }
                                                                                    >
                                                                                        <ListItemAvatar>
                                                                                            <Avatar>
                                                                                                <FilePresentTwoToneIcon />
                                                                                            </Avatar>
                                                                                        </ListItemAvatar>
                                                                                        <ListItemText
                                                                                            primary={x.Name}
                                                                                        />
                                                                                    </ListItem>
                                                                                )
                                                                            })
                                                                        }
                                                                        </Stack>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={3.5}>
                                                                    <label htmlFor="fileCompanyDocs">
                                                                        <input style={{ display: 'none' }} onChange={handleChangeFile} id="fileCompanyDocs" name="upload-company-docs" type="file" multiple />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload Company Docs</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                                <Grid item xs={8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        {
                                                                            companyFilesDetail.map(x => {
                                                                                return (
                                                                                    <ListItem sx={uploadFileListItemStyle} onClick={() => handleShowFile(x) }
                                                                                        secondaryAction={
                                                                                            <IconButton edge="end" aria-label="delete">
                                                                                                <DeleteOutlineTwoToneIcon />
                                                                                            </IconButton>
                                                                                        }
                                                                                    >
                                                                                        <ListItemAvatar>
                                                                                            <Avatar>
                                                                                                <FilePresentTwoToneIcon />
                                                                                            </Avatar>
                                                                                        </ListItemAvatar>
                                                                                        <ListItemText
                                                                                            primary={x.Name}
                                                                                        />
                                                                                    </ListItem>
                                                                                )
                                                                            })
                                                                        }
                                                                    </Stack>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={3.5}>
                                                                    <label htmlFor="fileOtherDocs">
                                                                        <input style={{ display: 'none' }} onChange={handleChangeFile} id="fileOtherDocs" name="upload-photo" type="file" multiple />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload Other Docs</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                                <Grid item xs={8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        {
                                                                            otherFilesDetail.map(x => {
                                                                                return (
                                                                                    <ListItem sx={uploadFileListItemStyle}
                                                                                        secondaryAction={
                                                                                            <IconButton edge="end" aria-label="delete">
                                                                                                <DeleteOutlineTwoToneIcon />
                                                                                            </IconButton>
                                                                                        }
                                                                                    >
                                                                                        <ListItemAvatar>
                                                                                            <Avatar>
                                                                                                <FilePresentTwoToneIcon />
                                                                                            </Avatar>
                                                                                        </ListItemAvatar>
                                                                                        <ListItemText
                                                                                            primary={x.Name}
                                                                                        />
                                                                                    </ListItem>
                                                                                )
                                                                            })
                                                                        }
                                                                    </Stack>
                                                                </Grid>
                                                            </Grid>
                                                            <Grid container direction="row" sx={uploadFileRowStyle} justifyContent="center" alignItems="stretch">
                                                                <Grid item xs={3.5}>
                                                                    <label htmlFor="fileWorld">
                                                                        <input style={{ display: 'none' }} onChange={handleChangeFile} id="fileWorld" name="upload-photo" type="file" multiple />
                                                                        <Fab sx={uploadFileStyle} color="secondary" size="small" component="span" aria-label="add" variant="extended">
                                                                            <CloudUploadIcon fontSize="large" sx={{ mr: 2 }} />
                                                                            <p>Upload World Check</p>
                                                                        </Fab>
                                                                    </label>
                                                                </Grid>
                                                                <Grid item xs={8.5}>
                                                                    <Stack direction="row" spacing={2} sx={{ overflowX: 'auto', height: '75px' }}>
                                                                        {
                                                                            worldFilesDetail.map(x => {
                                                                                return (
                                                                                    <ListItem sx={uploadFileListItemStyle}
                                                                                        secondaryAction={
                                                                                            <IconButton edge="end" aria-label="delete">
                                                                                                <DeleteOutlineTwoToneIcon />
                                                                                            </IconButton>
                                                                                        }
                                                                                    >
                                                                                        <ListItemAvatar>
                                                                                            <Avatar>
                                                                                                <FilePresentTwoToneIcon />
                                                                                            </Avatar>
                                                                                        </ListItemAvatar>
                                                                                        <ListItemText
                                                                                            primary={x.Name}
                                                                                        />
                                                                                    </ListItem>
                                                                                )
                                                                            })
                                                                        }
                                                                    </Stack>
                                                                </Grid>
                                                            </Grid>
                                                    </TabPanel>
                                                </SwipeableViews>
                                            </Box>
                                        </CardContent>
                                    </Card>
                                </Grid>
                                    <Grid item xs={12}>
                                        <Button style={{ display: selectedTabIndex == 1 ? 'none' : undefined }} type="submit" sx={{ margin: 1 }} variant="contained" color="primary"> Next</Button>
                                        <Button style={{ display: selectedTabIndex == 0 ? 'none' : undefined }} type="button" sx={{ margin: 1 }} variant="contained" color="primary" onClick={handleSave}> Save</Button>
                                        <Button onClick={() => onClickBack()} sx={{ margin: 1 }} style={{ display: selectedTabIndex == 0 ? 'none' : undefined }} type="button" variant="contained" color="primary"> Back</Button>
                                    <Button type="button"
                                        onClick={() => resetForm()} sx={{ margin: 1 }} variant="contained" color="secondary">
                                        Reset
                                    </Button>

                                </Grid>
                            </Grid>

                            {/*</form>*/}
                        </div>
                    </Container>
                    </Box>
                </form>
            </Modal>


        </>
    );
}

export default DirectorForm;
