import { Profile } from '../types/profile.type';

export function  ProfileService() {
    return {
         setProfile : (profile: Profile) => {
            if (profile != undefined) {
                sessionStorage.setItem('UserID', profile.UserID.toString());
                sessionStorage.setItem('RoleID', profile.RoleID.toString());
                sessionStorage.setItem('Token', profile.Token.toString());
                sessionStorage.setItem('Expiration', profile.Expiration.toString());
            }
        },
     getProfile : () => {
            let profile: Profile = {
                UserID: sessionStorage.getItem('UserID') ? parseInt(sessionStorage.getItem('UserID')!) : 0,
                RoleID: sessionStorage.getItem('RoleID') ? parseInt(sessionStorage.getItem('RoleID')!) : 0,
                Token: sessionStorage.getItem('Token')!,
                Expiration: new Date(sessionStorage.getItem('Expiration')!),
            };
            return profile;
        }
    }
}
